00670 bool GRPSCREEN::CreateViewport(XCHAR* ID, float posx, float posy, float width, float height, XDWORD canvasposx, XDWORD canvasposy, XDWORD canvaswidth, XDWORD canvasheight)
00671 {
00672   GRPVIEWPORT* viewport = new GRPVIEWPORT();
00673   if(!viewport)  return false;
00674 
00675   viewport->GetID()->Set(ID);
00676   viewport->SetPosition(posx, posy);
00677   viewport->SetSize(width, height);
00678 
00679   GRPPROPERTIES canvasproperties;
00680 
00681   canvasproperties.CopyPropertysFrom(this);
00682 
00683   canvasproperties.SetPosition(canvasposx, canvasposy);
00684 
00685   float cwidth  = (float)canvaswidth;
00686   float cheight = (float)canvasheight;
00687 
00688   if(width  > cwidth)  cwidth  = width;
00689   if(height > cheight) cheight = height;
00690 
00691   canvasproperties.SetSize((XDWORD)cwidth, (XDWORD)cheight);
00692 
00693   if(viewport->CreateCanvas(canvasproperties))
00694     {
00695        viewport->GetCanvas()->GetScreenZone()->Set((int)posx, (int)posy, (int)(posx + width), (int)(posy + height));
00696        viewports.Add(viewport);
00697 
00698         // --------------------------------------------------------------------------------------------------
00699         // Main Canvas is selected if the name is the name of the main viewport or there is only one viewport.
00700   
00701        if((!viewport->GetID()->Compare(GRPVIEWPORT_ID_MAIN,true))  || viewports.GetSize() == 1)
00702           {
00703             index_maincanvas  = viewports.GetSize()-1;
00704             maincanvas        = viewport->GetCanvas();
00705           }
00706 
00707     } else return false;
00708 
00709   return true;
00710  }
