00890 LRESULT CALLBACK GRPWINDOWSSCREEN::BaseWndProc(HWND hwnd, UINT msg, WPARAM wparam, LPARAM lparam)
00891 {
00892   switch(msg)
00893     {
00894       case WM_SYSCOMMAND      :  switch(wparam)
00895                                   {
00896                                     case SC_CLOSE         : break;
00897                                     case SC_SCREENSAVE    :
00898                                     case SC_MONITORPOWER  : return 0;
00899                                   }
00900                                 break;
00901 
00902       case WM_ENDSESSION      : break;
00903 
00904       case WM_CLOSE           : { GRPWINDOWSSCREEN* screen = GRPWINDOWSSCREEN::GetListScreens()->Get(hwnd);
00905                                   if(screen)
00906                                     {
00907                                       if(screen->IsBlockClose())
00908                                         {
00909                                           return 0;
00910                                         }
00911                                     }              
00912                                 }  
00913                                 break;
00914 
00915       case WM_DESTROY         : { int a=0;
00916                                   a++;
00917                                 }
00918                                 break;
00919 
00920       case WM_POWERBROADCAST  : //Computer is suspending
00921                                 if(wparam == PBT_APMSUSPEND)
00922                                   {
00923                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00924                                     if(mainprocwindows.GetAppMain())
00925                                       {
00926                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_SUSPEND);
00927                                       }
00928                                     #endif
00929                                   }
00930 
00931                                 break;
00932 
00933       case WM_QUERYENDSESSION : //Computer is shutting down
00934                                 if(lparam == 0)
00935                                   {
00936                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00937                                     if(mainprocwindows.GetAppMain())
00938                                       {
00939                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_REBOOT);
00940                                       }
00941                                     #endif
00942                                   }
00943 
00944                                 //User is logging off
00945                                 if((lparam & ENDSESSION_LOGOFF) == ENDSESSION_LOGOFF)
00946                                   {
00947                                     #ifdef GRP_MAINPROCCTRL_ACTIVE
00948                                     if(mainprocwindows.GetAppMain())
00949                                       {
00950                                         if(mainprocwindows.GetAppMain()->GetApplication()) mainprocwindows.GetAppMain()->GetApplication()->SetSystemChangeStatus(XSYSTEM_CHANGESTATUSTYPE_SESSION_LOGOFF);
00951                                       }
00952                                     #endif
00953                                   }
00954 
00955                                 break;
00956     }
00957 
00958   return DefWindowProc(hwnd, msg, wparam, lparam);
00959 }
