00463 void HASHSHA2::Sha2_256_Update(HASHSHA2_256_CTX* ctx, XBYTE* input, XDWORD size)
00464 {
00465   XDWORD  blocknb;
00466   XDWORD  new_len, rem_len, tmp_len;
00467   XBYTE*  shifted_input;
00468 
00469   tmp_len = HASHSHA2_256_BLOCK_SIZE - ctx->size;
00470   rem_len = size < tmp_len ? size : tmp_len;
00471 
00472   memcpy(&ctx->block[ctx->size], input, rem_len);
00473 
00474   if(ctx->size + size < HASHSHA2_256_BLOCK_SIZE)
00475     {
00476       ctx->size += size;
00477       return;
00478     }
00479 
00480   new_len = size - rem_len;
00481   blocknb = new_len / HASHSHA2_256_BLOCK_SIZE;
00482 
00483   shifted_input = input + rem_len;
00484 
00485   Sha2_256_Transf(ctx, ctx->block, 1);
00486   Sha2_256_Transf(ctx, shifted_input, blocknb);
00487 
00488   rem_len = new_len % HASHSHA2_256_BLOCK_SIZE;
00489 
00490   memcpy(ctx->block, &shifted_input[blocknb << 6], rem_len);
00491 
00492   ctx->size       = rem_len;
00493   ctx->totalsize += (blocknb + 1) << 6;
