00509 void HASHSHA2::Sha2_256_End(HASHSHA2_256_CTX* ctx, XBYTE* digest)
00510 {
00511   XDWORD  blocknb;
00512   XDWORD  pm_len;
00513   XDWORD  len_b;
00514   int     i;
00515 
00516   blocknb = (1 + ((HASHSHA2_256_BLOCK_SIZE - 9) < (ctx->size % HASHSHA2_256_BLOCK_SIZE)));
00517 
00518   len_b  = (ctx->totalsize + ctx->size) << 3;
00519   pm_len = blocknb << 6;
00520 
00521   memset(ctx->block + ctx->size, 0, pm_len - ctx->size);
00522   ctx->block[ctx->size] = 0x80;
00523   UNPACK32(len_b, ctx->block + pm_len - 4);
00524 
00525   Sha2_256_Transf(ctx, ctx->block, blocknb);
00526 
00527   for(i=0 ; i<8; i++)
00528     {
00529       UNPACK32(ctx->h[i], &digest[i << 2]);
00530     }
00531 
00532 }
