00665   XBYTE* shifted_input;
00666 
00667   tmp_len = HASHSHA2_512_BLOCK_SIZE - ctx->size;
00668   rem_len = size < tmp_len ? size : tmp_len;
00669 
00670   memcpy(&ctx->block[ctx->size], input, rem_len);
00671 
00672   if(ctx->size + size < HASHSHA2_512_BLOCK_SIZE)
00673     {
00674       ctx->size += size;
00675       return;
00676     }
00677 
00678   new_len = size - rem_len;
00679   blocknb = new_len / HASHSHA2_512_BLOCK_SIZE;
00680 
00681   shifted_input = input + rem_len;
