00707 void HASHSHA2::Sha2_512_End(HASHSHA2_512_CTX* ctx, XBYTE* digest)
00708 {
00709   XDWORD  blocknb;
00710   XDWORD  pm_len;
00711   XDWORD  len_b;
00712   int     i;
00713 
00714   blocknb = 1 + ((HASHSHA2_512_BLOCK_SIZE - 17) < (ctx->size % HASHSHA2_512_BLOCK_SIZE));
00715 
00716   len_b = (ctx->totalsize + ctx->size) << 3;
00717   pm_len = blocknb << 7;
00718 
00719   memset(ctx->block + ctx->size, 0, pm_len - ctx->size);
00720   ctx->block[ctx->size] = 0x80;
00721 
00722   UNPACK32(len_b, ctx->block + pm_len - 4);
00723 
00724   Sha2_512_Transf(ctx, ctx->block, blocknb);
00725 
00726   for(i=0 ; i<8; i++)
00727     {
00728       UNPACK64(ctx->h[i], &digest[i << 3]);
00729     }
00730 }
