00936   rem_len = len < tmp_len ? len : tmp_len;
00937 
00938   memcpy(&ctx->block[ctx->size], input, rem_len);
00939 
00940   if(ctx->size + len < HASHSHA2_224_BLOCK_SIZE)
00941     {
00942       ctx->size += len;
00943       return;
00944     }
