00402 bool INPCURSORMOTION::AddFromLine(INPCURSORMOTION_REDUCEDMODE reducedmode, int reducedfactor, int x1, int y1, int x2, int y2)
00403 {
00404   int x, xinc1, xinc2;
00405   int y, yinc1, yinc2;
00406   int i, deltax, deltay, npixels;
00407   int d, dinc1, dinc2;
00408 
00409   // Calculate deltax and deltay for initialisation
00410   deltax = abs(x2 - x1);
00411   deltay = abs(y2 - y1);
00412 
00413   // Initialize all vars based on which is the independent variable
00414   if(deltax>=deltay)
00415     {
00416       // x is independent variable }
00417       npixels   = deltax + 1;
00418       d         = (2 * deltay) - deltax;
00419       dinc1     = deltay >> 1;
00420       dinc2     = (deltay - deltax) >> 1;
00421       xinc1     = 1;
00422       xinc2     = 1;
00423       yinc1     = 0;
00424       yinc2     = 1;
00425     }
00426    else
00427     {
00428       // y is independent variable }
00429       npixels   = deltay + 1;
00430       d         = (2 * deltax) - deltay;
00431       dinc1     = deltax >> 1;
00432       dinc2     = (deltax - deltay) >> 1;
00433       xinc1     = 0;
00434       xinc2     = 1;
00435       yinc1     = 1;
00436       yinc2     = 1;
00437     }
00438 
00439   // Make sure x and y move in the right directions
00440   if(x1>x2)
00441     {
00442       xinc1 = -xinc1;
00443       xinc2 = -xinc2;
00444     }
00445 
00446   if(y1>y2)
00447     {
00448       yinc1 = -yinc1;
00449       yinc2 = -yinc2;
00450     }
00451 
00452   // Start drawing at
00453   x = x1;
00454   y = y1;
00455 
00456 
00457   int  steps    = CalculateReducedModeSteps(reducedmode,reducedfactor,npixels);
00458   int  incstep  = steps;
00459   bool addpoint = false;
00460 
00461   // Draw the pixels }
00462   for(i=0;i<npixels;i++)
00463     {
00464       if(incstep == steps)
00465         {
00466           addpoint = true;
00467           incstep  = 0;
00468         }
00469 
00470       if(addpoint)
00471             AddPoint((float)x, (float)y, 0);
00472        else incstep++;
00473 
00474       addpoint = false;
00475 
00476       if(d<0)
00477         {
00478           d = d + dinc1;
00479           x = x + xinc1;
00480           y = y + yinc1;
00481         }
00482        else
00483         {
00484           d = d + dinc2;
00485           x = x + xinc2;
00486           y = y + yinc2;
00487         }
00488     }
00489 
00490   return false;
00491 }
