00695 float INPCURSORMOTION::GetAngle()
00696 {
00697   if(points.GetSize() < 2) return 0;
00698 
00699   INPCURSORMOTIONPOINT* point[2];
00700 
00701   point[0] = GetFirstPoint();
00702   point[1] = GetLastPoint();
00703 
00704   double radians = atan2(point[1]->GetY()-point[0]->GetY() , point[1]->GetX()-point[0]->GetX());
00705 
00706   float angle = (float)((180*radians)/PI);
00707 
00708   if(angle<0) angle = 180 + (180+angle);
00709 
00710   return angle;
