00755 bool INPCURSORMOTION::IsInRect(int x, int y, int width, int height)
00756 {
00757   if(points.IsEmpty()) return false;
00758 
00759   int npoints = 0;
00760 
00761   for(int c=0;c<(int)points.GetSize();c++)
00762     {
00763       INPCURSORMOTIONPOINT* point = (INPCURSORMOTIONPOINT*)points.Get(c);
00764       if(point)
00765         {
00766           if((point->GetX() >= x) &&  (point->GetX() <= (x+width)) &&
00767              (point->GetY() >= y) &&  (point->GetY() <= (y+height))) npoints++;
00768         }
00769     }
00770 
00771   if(npoints == points.GetSize()) return true;
00772 
00773   return false;
