00874 INPCURSORMOTION_DIR INPCURSORMOTION::GetDirectionByAngle(bool indetail)
00875 {  
00876   if(points.GetSize() < 2) return INPCURSORMOTION_DIR_UNKNOWN;
00877 
00878   float angle = GetAngle();
00879 
00880   if(indetail)
00881     {
00882       if((angle > 337.5f) || (angle <=  22.5f))  return INPCURSORMOTION_DIR_RIGHT;
00883       if((angle >  22.5f) && (angle <=  67.5f))  return INPCURSORMOTION_DIR_RIGHTUP;
00884       if((angle >  67.5f) && (angle <= 112.5f))  return INPCURSORMOTION_DIR_UP;
00885       if((angle > 112.5f) && (angle <= 157.5f))  return INPCURSORMOTION_DIR_LEFTUP;
00886       if((angle > 157.5f) && (angle <= 202.5f))  return INPCURSORMOTION_DIR_LEFT;
00887       if((angle > 202.5f) && (angle <= 247.5f))  return INPCURSORMOTION_DIR_LEFTDOWN;
00888       if((angle > 247.5f) && (angle <= 292.5f))  return INPCURSORMOTION_DIR_DOWN;
00889       if((angle > 292.5f) && (angle <= 337.5f))  return INPCURSORMOTION_DIR_RIGHTDOWN;
00890     }
00891    else
00892     {
00893       if((angle > 270) || (angle <=  45))        return INPCURSORMOTION_DIR_RIGHT;
00894       if((angle >  45) && (angle <= 135))        return INPCURSORMOTION_DIR_UP;
00895       if((angle > 135) && (angle <= 180))        return INPCURSORMOTION_DIR_LEFT;
00896       if((angle > 180) && (angle <= 270))        return INPCURSORMOTION_DIR_DOWN;
00897     }
00898 
00899   return INPCURSORMOTION_DIR_UNKNOWN;
00900 }
