00914 INPCURSORMOTION_DIR INPCURSORMOTION::GetDirectionByDifferential(XDWORD* differential)
00915 {
00916   INPCURSORMOTION_DIR dir = INPCURSORMOTION_DIR_UNKNOWN;
00917 
00918   if(differential) (*differential) = 0;
00919 
00920   if(points.GetSize() < 2) return dir;
00921 
00922   int max_up    = 0;
00923   int max_down  = 0;
00924   int max_right = 0;
00925   int max_left  = 0;
00926 
00927   int x_origin  = (int)points.Get(0)->GetX();
00928   int y_origin  = (int)points.Get(0)->GetY();
00929 
00930   for(XDWORD c=1; c<points.GetSize(); c++)
00931     {
00932       max_right = __MAX(((int)points.Get(c)->GetX() - x_origin),  max_right);
00933       max_left  = __MAX((x_origin - (int)points.Get(c)->GetX()),  max_left);
00934 
00935       max_up    = __MAX(((int)points.Get(c)->GetY() - y_origin),  max_up);
00936       max_down  = __MAX((y_origin - (int)points.Get(c)->GetY()),  max_down);     
00937     }
00938   
00939   int x   = __MAX(max_right, max_left);
00940   int y   = __MAX(max_up   , max_down);
00941 
00942   if(x > y)
00943     {
00944       if(differential) (*differential) = x;
00945 
00946       dir = INPCURSORMOTION_DIR_LEFT;
00947       if(max_right > max_left) dir = INPCURSORMOTION_DIR_RIGHT;
00948     }
00949    else
00950     {
00951       if(differential) (*differential) = y;
00952 
00953       dir = INPCURSORMOTION_DIR_DOWN;
00954       if(max_up > max_down) dir = INPCURSORMOTION_DIR_UP;
00955     }
00956  
00957   return dir;
00958 }
