01028 int INPCURSORMOTION::CalculateReducedModeSteps(INPCURSORMOTION_REDUCEDMODE mode,int factor,int npixels)
01029 {
01030   int steps = 0;
01031 
01032   switch(mode)
01033     {
01034       case INPCURSORMOTION_REDUCEDMODE_NONE            :                                 break;
01035       case INPCURSORMOTION_REDUCEDMODE_BYSTEPS         : steps = factor;                 break;
01036       case INPCURSORMOTION_REDUCEDMODE_NMAX            : steps = (npixels/factor);       break;
01037       case INPCURSORMOTION_REDUCEDMODE_NMAXPERCENT     : steps = (npixels*factor)/100;   break;
01038     }
01039 
01040   return steps;
01041 }
