00963 bool MAINPROCANDROID::GetPackageResourcePath(struct android_app* app, XSTRING& path)
00964 {
00965   JNIEnv* env;
00966   JavaVM* vm     = app->activity->vm;
00967   bool    status = false;
00968 
00969   vm->AttachCurrentThread(&env, NULL);
00970 
00971   jclass    clazz   = env->GetObjectClass(app->activity->clazz);
00972   jmethodID methodj = env->GetMethodID(clazz, "getPackageResourcePath", "()Ljava/lang/String;");
00973 
00974   jstring   jstr    = (jstring)env->CallObjectMethod(app->activity->clazz, methodj);
00975   if(jstr != NULL)
00976     {
00977       const char* str = env->GetStringUTFChars(jstr, NULL);
00978       path =  str;
00979       env->ReleaseStringUTFChars(jstr, str);
00980 
00981       status = true;
00982     }
00983 
00984   vm->DetachCurrentThread();
00985 
00986   return status;
00987 }
