01002 bool MAINPROCANDROID::GetAbsolutePath(struct android_app* app, XSTRING& path)
01003 {
01004   JNIEnv* env;
01005   JavaVM* vm     = app->activity->vm;
01006   bool    status = false;
01007 
01008   vm->AttachCurrentThread(&env, NULL);
01009 
01010   jclass    activityclass   = env->GetObjectClass(app->activity->clazz);
01011   jmethodID getfilesdir     = env->GetMethodID(activityclass, "getFilesDir", "()Ljava/io/File;");
01012   jobject   file            = env->CallObjectMethod(app->activity->clazz, getfilesdir);
01013   jclass    fileclass       = env->FindClass("java/io/File");
01014   jmethodID getabsolutepath = env->GetMethodID(fileclass, "getAbsolutePath", "()Ljava/lang/String;");
01015 
01016   jstring jpath = (jstring)env->CallObjectMethod(file, getabsolutepath);
01017   if(jpath != NULL)
01018     {
01019       const char* str = env->GetStringUTFChars(jpath, NULL);
01020       path =  str;
01021       env->ReleaseStringUTFChars(jpath, str);
01022       status = true;
01023     }
01024 
01025   vm->DetachCurrentThread();
01026 
01027   return status;
01028 }
