01257 bool MAINPROCANDROID::OverturnAssetsToExternalLocation(XPATH& origin, XPATH& datapath, XPATH& target)
01258 {
01259   XFILEUNZIP* unzip = new XFILEUNZIP();
01260   if(!unzip) return false;
01261 
01262   XSTRING  assetsstring;
01263   bool     status  = false;
01264 
01265   assetsstring = __L("assets");
01266 
01267   XDIR* GEN_XFACTORY_CREATE(xdir, Create_Dir())
01268   if(xdir)
01269     {
01270       status = unzip->Open(origin);
01271 
01272       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Android Resources] ZIP File: %s"), origin.Get());
01273 
01274       if(status)
01275         {
01276           XFILECMPZIP* filecmp;
01277           int          index = 0;
01278 
01279           status = true;
01280 
01281           do{
01282               filecmp = unzip->GotoFile(index);
01283               if(filecmp)
01284                 {
01285                   XPATH targetpath;
01286 
01287                   targetpath  = datapath;
01288                   targetpath.Slash_Add();
01289                   targetpath += filecmp->GetName();
01290 
01291                   targetpath.SetOnlyPath();
01292 
01293                   int position=targetpath.Find(assetsstring,true);
01294                   if(position!=-1)
01295                     {
01296                       XPATH targetfile;
01297                       XPATH namefile;
01298 
01299                       namefile = filecmp->GetName();
01300                       namefile.SetOnlyNamefileExt();
01301 
01302                       //targetpath.DeleteCharacters(position, assetsstring.GetSize()+1);
01303 
01304                       targetfile  = targetpath;
01305                       targetfile += namefile.Get();
01306 
01307                       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01308                       if(xfile)
01309                         {
01310                           //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Android Resources] ZIP descompress File: %s"), targetfile.Get());
01311 
01312                           if(!xfile->Exist(targetfile.Get()))
01313                             {
01314                               xdir->Make(targetpath.Get(), true);
01315                               status = unzip->DecompressFile(filecmp,targetpath,namefile.Get());
01316                             }
01317 
01318                           GEN_XFACTORY.Delete_File(xfile);
01319                         }
01320                     }
01321 
01322                   delete filecmp;
01323 
01324                   index++;
01325 
01326                 } else break;
01327 
01328             } while(status);
01329 
01330           unzip->Close();
01331 
01332         }
01333 
01334       GEN_XFACTORY.Delete_Dir(xdir);
01335     }
01336 
01337   delete unzip;
01338 
01339   return status;
01340 }
