01354 bool MAINPROCANDROID::AssetsDir_CreateAll(XPATH& origin)
01355 {
01356   AssetsDir_DeleteAll();
01357 
01358   XPATH       targetpath;
01359   XFILEUNZIP* unzip   = new XFILEUNZIP();
01360   XSTRING     assetsstring;
01361   bool        status  = false;
01362 
01363   if(!unzip) return false;
01364 
01365   assetsstring = __L("assets");
01366 
01367   status = unzip->Open(origin);
01368   if(status)
01369     {
01370       XFILECMPZIP* filecmp;
01371       int          index = 0;
01372 
01373       status = true;
01374 
01375       do{ filecmp = unzip->GotoFile(index);
01376           if(filecmp)
01377             {
01378               targetpath.Empty();
01379               
01380               targetpath += filecmp->GetName();
01381 
01382               targetpath.SetOnlyPath();
01383 
01384               int position=targetpath.Find(assetsstring,true);
01385               if(position!=-1)
01386                 {   
01387                   bool found = false;
01388              
01389                   targetpath.DeleteCharacters(position, assetsstring.GetSize()+1);   
01390 
01391                   for(int c=0; c<assetsdirs.GetSize(); c++)
01392                     {
01393                       XPATH* xpath;
01394 
01395                       xpath = assetsdirs.Get(c);
01396                       if(!xpath->Compare(targetpath)) 
01397                         {
01398                           found = true;
01399                           break;                               
01400                         }
01401                     }               
01402 
01403                   if(!found)
01404                     {
01405                       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Assets XPATH: %s"), targetpath.Get());
01406                       
01407                       AssetsDir_Add(&targetpath);                                                                                                  
01408                     }
01409                 } 
01410 
01411               index++;
01412 
01413               delete filecmp;
01414 
01415             } else break;
01416 
01417         } while(status);
01418 
01419       unzip->Close();        
01420     }
01421       
01422   delete unzip;
01423 
01424   return status;
01425 }
