00882 static void Signal_Handler(int sig)
00883 {
00884   XSTRING signalstr;
00885   XSTRING description;
00886   XSTRING string;
00887   bool    iserror = false;
00888 
00889   allexceptiontext.Empty();
00890 
00891   #ifdef APP_ACTIVE
00892   APPBASE* app = NULL;
00893   if(mainproclinux.GetAppMain()) app = mainproclinux.GetAppMain()->GetApplication();
00894 
00895   if(app)
00896     {
00897       if(app->GetExitType()!=APPBASE_EXITTYPE_UNKNOWN) return;
00898     }
00899   #endif
00900 
00901   switch(sig)
00902     {
00903       case SIGSEGV    : iserror     = true;
00904                         signalstr   = __L("SEGV");
00905                         description = __L("Segmentation fault.");
00906                         break;
00907 
00908       case SIGBUS     : iserror     = true;
00909                         signalstr   = __L("BUS");
00910                         description = __L("Bus fault.");
00911                         break;
00912 
00913       case SIGFPE     : iserror     = true;
00914                         signalstr   = __L("FPE");
00915                         description = __L("Erroneous arithmetic operation (such as divide by zero).");
00916                         break;
00917 
00918       case SIGABRT    : iserror     = true;
00919                         signalstr   = __L("ABRT");
00920                         description = __L("Error: Abnormal termination condition.");
00921                         break;
00922 
00923       case SIGSTKFLT  : iserror     = true;
00924                         signalstr   = __L("STKFLT");
00925                         description = __L("Error: Stack fault.");
00926                         break;
00927 
00928       case SIGILL     : iserror     = true;
00929                         signalstr   = __L("ILL");
00930                         description = __L("Error: Illegal instruction.");
00931                         break;
00932 
00933 
00934       case SIGHUP     : signalstr   = __L("HUP");
00935                         description = __L("Program hanged up.");
00936                         break;
00937 
00938       case SIGTERM    : signalstr   = __L("TERM");
00939                         description = __L("Termination request, sent to the program.");
00940                         break;
00941 
00942       case SIGINT     : signalstr   = __L("INT");
00943                         description = __L("External interrupt, usually initiated by the user.");
00944                         break;
00945 
00946       case SIGQUIT    : signalstr   = __L("QUIT");
00947                         description = __L("Terminal quit.");
00948                         break;
00949 
00950       case SIGTSTP    : signalstr   = __L("TSTP");
00951                         description = __L("Terminal stop signalstr.");
00952                         break;
00953 
00954       case SIGKILL    : // Not Available -> Kill (can't be caught or ignored).
00955                         break;
00956 
00957       case SIGSTOP    : // Not Available -> Stop executing(can't be caught or ignored).
00958                         break;
00959       case SIGUSR1    :
00960       case SIGUSR2    :   signalstr = __L("USR");
00961                           description = __L("User signal received.");
00962                           break;
00963     }
00964 
00965   Signal_Printf(iserror, __L("SIGNAL"), __L("[SIG%s] Error: %s"), signalstr.Get(), description.Get());
00966 
00967   #ifdef APP_ACTIVE
00968   if(app)
00969     {
00970       if(app->GetTimerGlobal())
00971         {
00972           XSTRING string2;
00973 
00974           app->GetTimerGlobal()->GetMeasureString(string2, true);
00975           Signal_Printf(false, NULL,__L("Time working: %s."), string2.Get());
00976         }
00977     }
00978   #endif
00979 
00980   switch(sig)
00981     {
00982       case SIGSEGV    :
00983       case SIGBUS     :
00984       case SIGFPE     :
00985       case SIGABRT    :
00986       case SIGSTKFLT  :
00987       case SIGILL     : { Signal_PrintfStackTrace();
00988 
00989                           string.Format(__L("SIGNAL %s: %s"), signalstr.Get(), description.Get());
00990 
00991                           #ifdef DIO_ALERTS_ACTIVE
00992                           DIOALERT* alert = GEN_DIOALERTS.CreateAlert(DIOALERTLEVEL_DANGER, string.Get(), allexceptiontext.Get());
00993                           if(alert)
00994                             {
00995                               GEN_DIOALERTS.Send(DIOALERTSSENDER_ALL, DIOALERTS_CONDITIONS_ID_GENINTERN_EXCEPTION, alert);
00996                               delete alert;
00997                             }
00998                           #endif
00999 
01000                           allexceptiontext.Empty();
01001 
01002                           fprintf(stderr," \n");
01003 
01004                           #ifdef APP_ACTIVE
01005                           if(app)
01006                             {
01007                               app->SetExitType(APPBASE_EXITTYPE_BY_SERIOUSERROR);
01008                               app->AppProc_End();
01009                             }
01010                           #endif
01011 
01012                           signal(sig, SIG_DFL);
01013                           kill(getpid(), sig);
01014 
01015                           //exit(sig);
01016                         }
01017                         break;
01018 
01019 
01020       case SIGINT     :
01021                         #ifdef APP_ACTIVE
01022                         if(app) app->SetExitType(APPBASE_EXITTYPE_BY_USER);
01023                         #endif
01024                         allexceptiontext.Empty();
01025                         break;
01026 
01027       case SIGQUIT    :
01028                         #ifdef APP_ACTIVE
01029                         if(app) app->SetExitType(APPBASE_EXITTYPE_BY_SHUTDOWN);
01030                         #endif
01031                         allexceptiontext.Empty();
01032                         break;
01033 
01034       case SIGHUP     :
01035       case SIGTERM    : { XCHAR previous;
01036                           XCHAR actual;
01037 
01038                           #ifdef APP_ACTIVE
01039                           if(app)
01040                             {
01041                               if(app->GetExitType()!=APPBASE_EXITTYPE_UNKNOWN) return;
01042                             }
01043                           #endif
01044 
01045                           if(Signal_RunLevel(previous, actual))
01046                             {
01047                               if(actual == __C('0'))
01048                                 {
01049                                   #ifdef APP_ACTIVE
01050                                   if(app) app->SetExitType(APPBASE_EXITTYPE_BY_SHUTDOWN);
01051                                   #endif
01052                                   allexceptiontext.Empty();
01053                                   break;
01054                                 }
01055                             }
01056 
01057                           #ifdef APP_ACTIVE
01058                           if(app) app->SetExitType(APPBASE_EXITTYPE_BY_USER);
01059                           #endif
01060                           allexceptiontext.Empty();
01061                         }
01062                         break;
01063     }
01064 }
