01081 bool Signal_Printf(bool iserror, XCHAR* title, XCHAR* mask, ...)
01082 {
01083   XSTRING outstring;
01084 
01085   va_list arg;
01086 
01087   va_start(arg, mask);
01088 
01089   outstring.FormatArg(mask, &arg);
01090 
01091   va_end(arg);
01092 
01093   bool do_log = false;
01094 
01095   #ifdef XLOG_ACTIVE
01096   if(GEN_XLOG.IsActive())
01097     {
01098       GEN_XLOG.SetFilters(__L(""));
01099 
01100       if(GEN_XLOG.AddEntry(iserror?XLOGLEVEL_ERROR:XLOGLEVEL_WARNING, title, false, outstring.Get()))
01101         {
01102           do_log = true;
01103         }
01104     }
01105   #endif
01106 
01107   allexceptiontext += outstring;
01108   allexceptiontext += __L("\r\n");
01109 
01110   #ifdef XTRACE_ACTIVE
01111   if(!do_log)
01112     {
01113       int level = iserror?4:0;
01114 
01115      XTRACE_PRINTCOLOR(level, outstring.Get());
01116     }
01117   #endif
01118 
01119   return true;
01120 }
