01135 static inline void Signal_PrintfStackTrace(FILE *out, unsigned int max_frames)
01136 {
01137   void*  addrlist[max_frames+1];        // storage array for stack trace address data
01138   XDWORD addrlen = 0;
01139 
01140   memset(addrlist,0, sizeof(addrlist));
01141 
01142   addrlen = backtrace(addrlist, sizeof(addrlist) / sizeof(void*));  // retrieve current stack addresses
01143 
01144   if(addrlen == 0)
01145     {
01146       Signal_Printf(true, NULL, __L("Stack trace: Not available."));
01147       return;
01148 
01149     } else Signal_Printf(true, NULL, __L("Stack trace: (%d) calls"),addrlen);
01150 
01151   // resolve addresses into strings containing "filename(function+address)",
01152   // Actually it will be ## program address function + offset
01153   // this array must be free()-ed
01154   char** symbollist = backtrace_symbols( addrlist, addrlen );
01155 
01156   size_t funcnamesize = 1204;
01157   char   funcname[1024];
01158 
01159   // iterate over the returned symbol lines. skip the first, it is the
01160   // address of this function.
01161   for(XDWORD i = 0; i < addrlen; i++)
01162     {
01163       char* begin_name   = NULL;
01164       char* begin_offset = NULL;
01165       char* end_offset   = NULL;
01166 
01167       XSTRING   beginname;
01168       XSTRING   beginoffset;
01169       XSTRING   endoffset;
01170       XSTRING   symbol;
01171 
01172       // find parentheses and +address offset surrounding the mangled name
01173 
01174       for(char *p = symbollist[i]; *p; ++p)
01175         {
01176          if(*p == '(')
01177            {
01178              begin_name = p;
01179            }
01180           else
01181            {
01182              if(*p == '+')
01183                {
01184                  begin_offset = p;
01185                }
01186               else
01187                {
01188                  if ( *p == ')' && ( begin_offset || begin_name ))  end_offset = p;
01189                }
01190            }
01191         }
01192 
01193       if(begin_name && end_offset && (begin_name < end_offset))
01194         {
01195           *begin_name++   = '\0';
01196           *end_offset++   = '\0';
01197 
01198           if(begin_offset) *begin_offset++ = '\0';
01199 
01200           int   status = -1;
01201           char* ret    = NULL;
01202           // mangled name is now in [begin_name, begin_offset) and caller
01203           // offset in [begin_offset, end_offset). now apply
01204           // __cxa_demangle():
01205 
01206           //#ifndef HW_PC_OLD
01207           abi::__cxa_demangle(begin_name, funcname, &funcnamesize, &status);
01208 
01209           char* fname = begin_name;
01210           if(status == 0)  fname = ret;
01211           beginname   = fname;
01212           //#endif
01213 
01214           endoffset   = end_offset;
01215           symbol      = symbollist[i];
01216 
01217           if(begin_offset)
01218             {
01219               beginoffset = begin_offset;
01220               Signal_Printf(true, NULL, __L("%-40s (%-40s  + %-6s) %s"), symbol.Get(), beginname.Get(), beginoffset.Get(), endoffset.Get());
01221             }
01222            else
01223             {
01224               Signal_Printf(true, NULL, __L("%-40s (%-40s    %-6s) %s"), symbol.Get(), beginname.Get(), __L(""), endoffset.Get());
01225             }
01226         }
01227        else
01228         {
01229           symbol      = symbollist[i];
01230           // couldn't parse the line? print the whole line.
01231           Signal_Printf(true, NULL, __L("%-40s"), symbol.Get());
01232         }
01233     }
01234 
01235   free(symbollist);
01236 }
