01238 
01251 bool Signal_RunLevel(XCHAR& previous, XCHAR& actual)
01252 {
01253   struct utmp* ut;
01254   bool         status = false;
01255 
01256   previous = 0;
01257   actual   = 0;
01258 
01259   setutent();
01260 
01261   while((ut = getutent()) != NULL)
01262     {
01263       if(ut->ut_type == RUN_LVL)
01264         {
01265           previous = (XCHAR)(ut->ut_pid / 256);
01266           if(!previous) previous = __C('N');
01267 
01268           actual   = (XCHAR)(ut->ut_pid % 256);
01269 
01270           status = true;
01271         }
01272     }
01273 
01274   endutent();
01275 
01276   return status;
01277 }
