00503 bool MYSQL_QUERY::BindParametersToResult()
00504 {
00505   MYSQL_RES* mysqlresultfromstmt = mysql_stmt_result_metadata(stmt);
00506   if(!mysqlresultfromstmt)  return true;   // This statement gives no result set
00507 
00508   // Prepare memory
00509   int ncols=mysql_num_fields(mysqlresultfromstmt);
00510   if(!ncols) return true;
00511 
00512   MYSQL_RESULT* result = static_cast<MYSQL_RESULT*>(this->result);
00513   result->bind_results_size = ncols;
00514 
00515   result->bindresults = NULL;
00516   result->bindresults = new MYSQL_BIND[ncols];
00517   if(!result->bindresults)
00518     {
00519       this->database->Error(__L("Not enought memory"));
00520       return false;
00521     }
00522 
00523   memset(result->bindresults, 0, ncols*sizeof(MYSQL_BIND));
00524 
00525   // Iterate
00526 
00527   for(int e=0; e<ncols; e++)
00528     {
00529       MYSQL_FIELD* field = mysql_fetch_field(mysqlresultfromstmt);
00530 
00531       result->bindresults[e].buffer_type      = field->type;
00532       switch(field->type)
00533         {
00534           case MYSQL_TYPE_DATETIME  : result->bindresults[e].buffer = new MYSQL_TIME;                   break;
00535                             default : result->bindresults[e].buffer = new unsigned char[field->length]; break;
00536         }
00537 
00538       if(!result->bindresults[e].buffer)
00539         {
00540           delete (result->bindresults);
00541           return false;
00542         }
00543 
00544       result->bindresults[e].buffer_length    = field->length;
00545       result->bindresults[e].is_unsigned      = 0;
00546       result->bindresults[e].is_null          = 0;
00547       result->bindresults[e].length           = 0;
00548     }
00549 
00550   //ind param block
00551 
00552   int RC = mysql_stmt_bind_result(stmt, result->bindresults);
00553   if(RC)
00554     {
00555       DB_SQL_ERROR* error=new DB_SQL_ERROR(DB_SQL_ERROR_TYPE_STATEMENT_ERROR);
00556       if(!error) return false;
00557 
00558       error->description.Set(mysql_stmt_error(stmt));
00559       database->GetErrorList()->Add(error);
00560       if (database->IsTransactionStarted()) database->Rollback();
00561 
00562       return true;
00563     }
00564 
