00550 
00551   SNDFILE* soundfile = SNDFILE::Create(path);
00552   if(!soundfile)
00553     {
00554       delete item;
00555       return NULL;
00556     }
00557 
00558   item->SetStatus(SNDITEM_STATUS_NONE);
00559   item->SetSoundFile(soundfile);
00560 
00561   sounditems.Add(item);
00562   
00563   return item;
