00661 bool SNDFACTORY::Sound_IsAnyActive()
00662 { 
00663   if(!soundactive)
00664     {
00665       return false;
00666     }
00667 
00668   XDWORD counter = 0;
00669 
00670   for(XDWORD c=0; c<sounditems.GetSize(); c++)
00671     {
00672       SNDITEM* item = sounditems.Get(c);
00673       if(item)
00674         {
00675           if((item->GetStatus() != SNDITEM_STATUS_END) && 
00676              (item->GetStatus() != SNDITEM_STATUS_NONE))
00677             {
00678               counter++;
00679             }
00680         }
00681     }
00682 
00683   return counter?true:false;                     
00684 }
