00656 bool SNDOPENALFACTORY::Sound_WaitToEnd(SNDITEM* item, int maxtimeout, SNDFACTORY_WAITFUNCTION waitfunction)
00657 {
00658   if(!soundactive)
00659     {
00660       return false;
00661     }
00662 
00663   if(!item)
00664     {
00665       return false;
