00667 
00668   XTIMER* timeout = NULL;
00669   bool    status  =  true;
00670   
00671   timeout = GEN_XFACTORY.CreateTimer();
00672   if(!timeout)
00673     {
00674       return false;
00675     }
00676 
00677   timeout->Reset();
00678 
00679   while(item->GetStatus() != SNDITEM_STATUS_END)
00680     {
00681       if(maxtimeout)
00682         {
00683           if(timeout->GetMeasureMilliSeconds() >= maxtimeout)
00684             {
00685               status = false;  
00686               break;
00687             }
00688         }
00689 
00690       if(waitfunction)
00691         {
00692           if(!waitfunction(item))
00693             {
00694               status = false;
00695               break;
00696             }
00697         }
00698        else
00699         { 
00700           GEN_XSLEEP.MilliSeconds(100);
00701         }
00702     }
00703 
00704   GEN_XFACTORY.DeleteTimer(timeout);
00705 
00706   if(waitfunction)
00707     {
00708       if(!waitfunction(item))
00709         {
00710           status = false;
00711         }
00712     }
00713 
00714   return status;
00715 }
