01020 bool SNDOPENALFACTORY::GenerateBuffer(SNDOPENALPLAYITEM* playitem, SNDNOTE* soundnote)
01021 {
01022   if(!soundactive)
01023     {
01024       return false;
01025     }
01026 
01027   if(!playitem || !soundnote)
01028     {
01029       return false;
01030     }
01031 
01032   XDWORD           samplerate = 10000;
01033   XDWORD           size       = 0;
01034   SNDOPENALSOURCE* source     = playitem->GetSource();
01035 
01036   if(!source)
01037     {
01038       return false;
01039     }  
01040 
01041   source->GetBuffer()->GetXBuffer()->Empty();
01042 
01043   size += source->GetBuffer()->GenerateNote(soundnote->GetFrequency(), soundnote->GetDuration()*2, samplerate);
01044   size += source->GetBuffer()->GenerateNote(soundnote->GetFrequency(), 2000, samplerate);
01045                                                             
01046   source->GetBuffer()->Assign(1, size, samplerate);
01047 
01048   return true;
01049 }
