01064 bool SNDOPENALFACTORY::GenerateBuffer(SNDOPENALPLAYITEM* playitem, SNDFILE* soundfile)
01065 {
01066   if(!soundactive)
01067     {
01068       return false;
01069     }
01070 
01071   if(!playitem || !soundfile)
01072     {
01073       return false;
01074     }
01075 
01076   XDWORD           size       = 0;
01077   SNDOPENALSOURCE* source     = playitem->GetSource();
01078 
01079   source->GetBuffer()->GetXBuffer()->Add(soundfile->GetData());
01080 
01081   source->GetBuffer()->Assign(soundfile->GetChannels(), soundfile->GetData()->GetSize(), soundfile->GetSampleRate());
01082 
01083   return true;
01084 }
