01098 void SNDOPENALFACTORY::ThreadPlay(void* param)
01099 {  
01100   SNDOPENALFACTORY* sndopenal = (SNDOPENALFACTORY*)param;
01101   if(!sndopenal) return;  
01102 
01103   if(sndopenal->IsSoundActive())
01104     {
01105       sndopenal->Update();
01106     }
01107 }
