00596 bool SCRIPT_LNG_G_VAR::ConvertToXVariant(XVARIANT& variant)
00597 { 
00598   switch(type)
00599     {
00600       case SCRIPT_LNG_G_TOKENIREPS_CHAR     : { XSTRING str;
00601 
00602                                                 str.Format(__L("%c"), GetValueCharacter());
00603                                                 variant = (XCHAR)str.Get()[0];
00604                                               }
00605                                               break;
00606 
00607       case SCRIPT_LNG_G_TOKENIREPS_INT      : variant = GetValueInteger();        break;
00608       case SCRIPT_LNG_G_TOKENIREPS_STRING   : variant = GetValueString()->Get();  break;
00609 
00610                                 default     : variant.Set();
00611                                               return false;
00612     }
00613  
00614   return true;
00615 }
