00629 bool SCRIPT_LNG_G_VAR::ConvertFromXVariant(XVARIANT& variant)
00630 {
00631   Clear();
00632 
00633   switch(variant.GetType())
00634     {
00635       case XVARIANT_TYPE_NULL           : break;
00636       
00637       case XVARIANT_TYPE_INTEGER        : SetType(SCRIPT_LNG_G_TOKENIREPS_INT);
00638                                           SetValueInteger(variant);
00639                                           break;
00640 
00641       case XVARIANT_TYPE_CHAR           : { XSTRING str;
00642                                             XCHAR   character = 0x000;
00643 
00644                                             str = (const XSTRING&)variant;
00645 
00646                                             if(!str.IsEmpty())
00647                                               {
00648                                                 character = str.Get()[0];
00649                                                 SetType(SCRIPT_LNG_G_TOKENIREPS_CHAR);
00650                                                 SetValueCharacter(character);
00651                                               }
00652                                           }
00653                                           break;
00654 
00655       case XVARIANT_TYPE_FLOAT          : return false;
00656 
00657       case XVARIANT_TYPE_STRING         : { XSTRING* string = new XSTRING();
00658                                             if(!string) break;
00659 
00660                                             (*string) = (const XSTRING&)variant;
00661                                             SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
00662                                             SetHaveReservedSize(true);
00663                                             SetValueString(string);
00664                                           }
00665                                           break;
00666 
00667       case XVARIANT_TYPE_DATE           :
00668       case XVARIANT_TYPE_TIME           :
00669       case XVARIANT_TYPE_DATETIME       : return false;
00670     }
00671  
00672   return true;
00673 }
