00685 bool SCRIPT_LNG_G_VAR::Clear()
00686 {
00687   name.Empty();
00688 
00689   if((value.string) && (type == SCRIPT_LNG_G_TOKENIREPS_STRING) &&  (HaveReservedSize()))
00690     {
00691       delete value.string;
00692       value.string    = NULL;
00693     }
00694 
00695   type                = SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK;
00696   value.integer       = 0;
00697 
00698   havereservedsize    = false;
00699   isarg               = false;
00700   isreturnvalue       = false;
00701 
00702   return true;
00703 }
