00921 SCRIPT_LNG_G::SCRIPT_LNG_G()
00922 {
00923   Clean();
00924 
00925   type = SCRIPT_TYPE_G;
00926 
00927   functioncallstack.SetIsMulti(true);
00928 
00929   DeleteVarsExec();
00930 
00931   AddCommand(__L("if")      , SCRIPT_LNG_G_TOKENIREPS_IF);
00932   AddCommand(__L("else")    , SCRIPT_LNG_G_TOKENIREPS_ELSE);
00933   AddCommand(__L("for")     , SCRIPT_LNG_G_TOKENIREPS_FOR);
00934   AddCommand(__L("do")      , SCRIPT_LNG_G_TOKENIREPS_DO);
00935   AddCommand(__L("while")   , SCRIPT_LNG_G_TOKENIREPS_WHILE);
00936   AddCommand(__L("char")    , SCRIPT_LNG_G_TOKENIREPS_CHAR);
00937   AddCommand(__L("int")     , SCRIPT_LNG_G_TOKENIREPS_INT);
00938   AddCommand(__L("string")  , SCRIPT_LNG_G_TOKENIREPS_STRING);
00939   AddCommand(__L("return")  , SCRIPT_LNG_G_TOKENIREPS_RETURN);
00940   AddCommand(__L("switch")  , SCRIPT_LNG_G_TOKENIREPS_SWITCH);
00941   AddCommand(__L("break")   , SCRIPT_LNG_G_TOKENIREPS_BREAK);
00942   AddCommand(__L("case")    , SCRIPT_LNG_G_TOKENIREPS_CASE);
00943   AddCommand(__L("")        , SCRIPT_LNG_G_TOKENIREPS_END);
00944 
00945   AddInternalLibraries();
00946 }
