00984 int SCRIPT_LNG_G::Run(int* returnval)
00985 {
00986   if(script.IsEmpty()) return SCRIPT_ERRORCODE_INTERNALERROR;
00987 
00988   isrunwiththread = false;
00989   if(thread) isrunwiththread = thread->IsRunning();
00990 
00991   errorcode  = SCRIPT_ERRORCODE_NONE;
00992   iscancelexec = false;
00993   breakfound   = false;
00994   ipprg        = script.Get();
00995 
00996   
00997   SCRIPT_LNG_G_VAR* versionvar = new SCRIPT_LNG_G_VAR();
00998   if(versionvar)
00999     {
01000       XSTRING* version = new XSTRING();
01001       if(version)
01002         {
01003           version->Format(__L("%d.%d.%d"), SCRIPT_LNG_G_VERSION, SCRIPT_LNG_G_SUBVERSION, SCRIPT_LNG_G_SUBVERSIONERR);
01004        
01005           versionvar->SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
01006           versionvar->SetName(__L("VERSION"));    
01007           versionvar->SetValueString(version);    
01008           versionvar->SetHaveReservedSize(true);
01009           versionvar->SetIsArg(false);
01010 
01011           globalvars.Add(versionvar);
01012         }
01013        else
01014         {
01015           delete versionvar;
01016         }
01017     }
01018   
01019   PreScan();
01020 
01021   if(errorcode == SCRIPT_ERRORCODE_NONE)
01022     {
01023       ipprg = FindFunction(__L("main"));    // Find program starting point.
01024       if(ipprg)
01025         {
01026           ipprg--;
01027           XSTRING::Set(currenttoken,__L("main"));
01028 
01029           Call();
01030 
01031         } else errorcode = SCRIPT_LNG_G_ERRORCODE_FUNC_UNDEF;
01032     }
01033 
01034   if(errorcode == SCRIPT_ERRORCODE_NONE)
01035     {
01036       if(returnval)
01037         {
01038           (*returnval) = returnvalue.GetValueInteger();
01039         }
01040     }
01041 
01042   
01043   DeleteVarsExec();
01044 
01045   return errorcode;
01046 }
