01061 bool SCRIPT_LNG_G::AddCommand(XCHAR* command,SCRIPT_LNG_G_TOKENIREPS token)
01062 {
01063   SCRIPT_LNG_G_COMMAND* _command = new SCRIPT_LNG_G_COMMAND();
01064   if(!_command) return false;
01065 
01066   if(!_command->Set(command, token)) return false;
01067 
01068   commands.Add(_command);
01069 
01070   return true;
01071 }
