01105 int SCRIPT_LNG_G::GetFuncParams(SCRIPT_LNG_G_VAR* params)
01106 {
01107   int count = 0;
01108 
01109   GetToken();
01110 
01111   if(currenttoken[0] != __C('('))
01112     {
01113       HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
01114       return count;
01115     }
01116 
01117   do{ SCRIPT_LNG_G_VAR* param = new SCRIPT_LNG_G_VAR();
01118       if(param)
01119         {
01120           EvalExp((*param));
01121 
01122           params[count].Set(param);
01123 
01124           /*  
01125           if(param->IsReturnValue())
01126                  params[count].SetHaveReservedSize(true);
01127             else params[count].SetHaveReservedSize(false);
01128           */
01129 
01130           GetToken();
01131 
01132           count++;
01133 
01134           param->SetHaveReservedSize(false);
01135 
01136           delete param;
01137         }
01138 
01139     } while(currenttoken[0] == __C(','));
01140 
01141   return count;
01142 }
