01154 void SCRIPT_LNG_G::NotFuncParams()
01155 {
01156   GetToken();
01157   if(currenttoken[0] != __C('('))
01158     {
01159       HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
01160       return;
01161     }
01162 
01163   GetToken();
01164   if(currenttoken[0] != __C(')'))
01165     {
01166       HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
01167       return;
01168     }
01169 }
