01217 bool SCRIPT_LNG_G::HaveError(int errorcode)
01218 {
01219   if(errorcode != SCRIPT_ERRORCODE_NONE)
01220     {
01221       static XCHAR* errorstr[]= { __L("None")                       ,
01222                                   __L("Internal error")             ,
01223                                   __L("Syntax error")               ,
01224                                   __L("Insufficient parameters")    ,
01225                                   __L("No expression present")      ,
01226                                   __L("Not a variable")             ,
01227                                   __L("Duplicate variable name")    ,
01228                                   __L("Duplicate function name")    ,
01229                                   __L("Semicolon expected")         ,
01230                                   __L("Unbalanced braces")          ,
01231                                   __L("Function undefined")         ,
01232                                   __L("Type specifier expected")    ,
01233                                   __L("Return without call")        ,
01234                                   __L("Parentheses expected")       ,
01235                                   __L("While expected")             ,
01236                                   __L("Closing quote expected")     ,
01237                                   __L("Division by zero")           ,
01238                                   __L("{ expected (control statements must use blocks)"),
01239                                   __L("Colon expected")             ,
01240                                   __L("Break by user")
01241                                 };
01242 
01243       SCRIPT_XEVENT xevent(this,(errorcode==SCRIPT_LNG_G_ERRORCODE_USERBREAK)?SCRIPT_XEVENT_TYPE_BREAK:SCRIPT_XEVENT_TYPE_ERROR);
01244 
01245 
01246       XDWORD sizecharnow = (int)(ipprg - script.Get());
01247       XDWORD sizechar    = 0;
01248       int nline       = 0;
01249 
01250       for(int c=0;c<xfiletxt->GetNLines();c++)
01251         {
01252           sizechar += (xfiletxt->GetLine(c)->GetSize()+2);
01253 
01254           if(sizecharnow<=sizechar)
01255             {
01256               nline = c+1;
01257               break;
01258             }
01259         }
01260 
01261       xevent.SetError(errorcode);
01262       xevent.GetErrorText()->Set(errorstr[errorcode]);
01263       xevent.GetCurrentToken()->Set(currenttoken);
01264       xevent.SetNLine(nline);
01265 
01266       XTRACE_PRINTCOLOR(4, __L("Script [%s] ERROR %d: %s, line %d \"%s\"") , namescript.Get(), errorcode, errorstr[errorcode], nline, currenttoken);
01267 
01268       PostEvent(&xevent);      
01269       iscancelexec = true;
01270 
01271       this->errorcode = errorcode;
01272     }
01273 
01274   return (errorcode==SCRIPT_ERRORCODE_NONE)?false:true;
01275 }
