01287 bool SCRIPT_LNG_G::DeleteVarsExec()
01288 {
01289   if(!globalvars.IsEmpty())
01290     {
01291       globalvars.DeleteContents();
01292       globalvars.DeleteAll();
01293     }
01294 
01295   if(!functiontable.IsEmpty())
01296     {
01297       functiontable.DeleteContents();
01298       functiontable.DeleteAll();
01299     }
01300 
01301   if(!localvarsstack.IsEmpty())
01302     {
01303       localvarsstack.DeleteContents();
01304       localvarsstack.DeleteAll();
01305     }
01306 
01307   if(!functioncallstack.IsEmpty()) 
01308     {
01309       functioncallstack.DeleteAll();
01310     }
01311 
01312   if(!nestscopestack.IsEmpty())    
01313     {
01314       nestscopestack.DeleteAll();
01315     }
01316 
01317   return true;
01318 }
