01391 bool SCRIPT_LNG_G::IsDelimiter(XCHAR c)
01392 {
01393   if(c==0) return true;
01394 
01395   XSTRING delimiter(__L("\x09\r !:;,+-<>'/*%^=()"));
01396 
01397   if(delimiter.FindCharacter(c)!=XSTRING_NOTFOUND) return true;
01398 
01399   return false;
01400 }
