01402 
01414 SCRIPT_LNG_G_TOKENIREPS SCRIPT_LNG_G::LookUpToken(XCHAR* s)
01415 {
01416   for(int c=0;c<(int)commands.GetSize();c++)
01417     {
01418       SCRIPT_LNG_G_COMMAND* command = (SCRIPT_LNG_G_COMMAND*)commands.Get(c);
01419       if(command)
01420         {
01421           if(!command->GetCommand()->Compare(s)) return command->GetToken();
01422         }
01423     }
01424 
01425   return SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK;
01426 }
