01438 void SCRIPT_LNG_G::PutBackToken()
01439 {
01440   XCHAR* token;
01441 
01442   token = currenttoken;
01443 
01444   for(; *token; token++)
01445     {
01446       ipprg--;
01447     }
01448 }
01449 
01450 
01462 void SCRIPT_LNG_G::EvalExp(SCRIPT_LNG_G_VAR& value)
01463 {
01464   GetToken();
01465 
01466   if((tokentype != SCRIPT_LNG_G_TOKENTYPES_STRING))
01467     {
01468       if(!currenttoken[0])
01469         {
01470           HaveError(SCRIPT_LNG_G_ERRORCODE_NO_EXP);
01471           return;
01472         }
01473     }
01474    else
01475     {
01476       XSTRING* string = new XSTRING();
01477       if(string)
01478         {
01479           (*string) = currenttoken;
01480 
01481           value.SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);          
01482           value.SetValueString(string);
01483           value.SetHaveReservedSize(true);
01484 
01485           value.SetIsReturnValue(false);
01486 
01487           return;
01488         }
01489     }
01490 
01491   if(currenttoken[0] == __C(';'))
01492     {
01493       value.Clear();
01494       return;
01495     }
01496 
01497   EvalExp0(value);
01498 
01499   PutBackToken();
01500 }
01501 
01502 
01514 void SCRIPT_LNG_G::EvalExp0(SCRIPT_LNG_G_VAR& value)
01515 {
01516   XSTRING                 tempttoken;
01517   SCRIPT_LNG_G_TOKENTYPES temptokentype;
01518 
01519   if(tokentype == SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER)
01520     {
01521        SCRIPT_LNG_G_VAR* variable = IsVariable(currenttoken);
01522        if(variable)
01523          {
01524            tempttoken    = currenttoken;
01525            temptokentype = tokentype;
01526 
01527            GetToken();
01528 
01529            if(currenttoken[0] == __C('='))
01530              {
01531                GetToken();
01532 
01533                if(tokentype == SCRIPT_LNG_G_TOKENTYPES_STRING)
01534                  {
01535                    XSTRING* string = new XSTRING();
01536                    if(string)
01537                      {
01538                        (*string) = currenttoken;
01539 
01540                        value.SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
01541                        value.SetValueString(string);
01542                        value.SetHaveReservedSize(true);
01543 
01544                        AssignVariable(tempttoken.Get(), value);
01545                     
01546                        GetToken();
01547                      }
01548                  }
01549                 else
01550                  {
01551                    EvalExp0(value);
01552                    AssignVariable(tempttoken.Get(), value);
01553                  }
01554 
01555                return;
01556              }
01557             else
01558              {
01559                if(variable->GetType() == SCRIPT_LNG_G_TOKENIREPS_STRING)
01560                  {
01561                    value.GetName()->Set(variable->GetName()->Get());
01562                    value.SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
01563                    value.SetValueString(variable->GetValueString());
01564                    
01565                    return;
01566                  }
01567                 else
01568                  {
01569                    PutBackToken();
01570 
01571                    XSTRING::Set(currenttoken,tempttoken.Get());
01572                    tokentype    = temptokentype;
01573                  }
01574              }
01575          }
01576     }
01577 
01578   EvalExp1(value);
01579 }
01580 
01581 
01593 void SCRIPT_LNG_G::EvalExp1(SCRIPT_LNG_G_VAR& value)
01594 {
01595   XCHAR   operation;
01596   XSTRING relops;
01597 
01598   relops.Format(__L("%c%c%c%c%c%c"), SCRIPT_LNG_G_DOUBLEOPERATOR_LT, SCRIPT_LNG_G_DOUBLEOPERATOR_LE, SCRIPT_LNG_G_DOUBLEOPERATOR_GT
01599                                    , SCRIPT_LNG_G_DOUBLEOPERATOR_GE, SCRIPT_LNG_G_DOUBLEOPERATOR_EQ, SCRIPT_LNG_G_DOUBLEOPERATOR_NE);
01600   EvalExp2(value);
01601 
01602   operation = currenttoken[0];
01603 
01604   if(relops.FindCharacter(operation)!=XSTRING_NOTFOUND)
01605     {
01606       GetToken();
01607 
01608       if(tokentype == SCRIPT_LNG_G_TOKENTYPES_STRING)
01609         {
01610           XSTRING* strpvalue = new XSTRING();
01611           if(strpvalue)
01612             {
01613               XSTRING* strvalue = value.GetValueString();
01614 
01615               SCRIPT_LNG_G_VAR pvalue;
01616 
01617               pvalue.SetValueString(strpvalue);
01618 
01619               EvalExp2(pvalue);
01620 
01621               switch(operation)
01622                 {
01623                   case SCRIPT_LNG_G_DOUBLEOPERATOR_LT:  value.SetValueInteger((strvalue->GetSize() <  strpvalue->GetSize())); break;
01624                   case SCRIPT_LNG_G_DOUBLEOPERATOR_LE:  value.SetValueInteger((strvalue->GetSize() <= strpvalue->GetSize())); break;
01625                   case SCRIPT_LNG_G_DOUBLEOPERATOR_GT:  value.SetValueInteger((strvalue->GetSize() >  strpvalue->GetSize())); break;
01626                   case SCRIPT_LNG_G_DOUBLEOPERATOR_GE:  value.SetValueInteger((strvalue->GetSize() >= strpvalue->GetSize())); break;
01627                   case SCRIPT_LNG_G_DOUBLEOPERATOR_EQ:  value.SetValueInteger(!strvalue->Compare((*strpvalue))?1:0);          break;
01628                   case SCRIPT_LNG_G_DOUBLEOPERATOR_NE:  value.SetValueInteger(strvalue->Compare((*strpvalue)));               break;
01629                 }
01630 
01631               delete strpvalue;
01632             }
01633         }
01634        else
01635         {
01636           SCRIPT_LNG_G_VAR partialvalue;
01637 
01638           EvalExp2(partialvalue);
01639 
01640           switch(operation)
01641             {
01642               case SCRIPT_LNG_G_DOUBLEOPERATOR_LT:  value.SetValueInteger((value.GetValueInteger() <  partialvalue.GetValueInteger())); break;
01643               case SCRIPT_LNG_G_DOUBLEOPERATOR_LE:  value.SetValueInteger((value.GetValueInteger() <= partialvalue.GetValueInteger())); break;
01644               case SCRIPT_LNG_G_DOUBLEOPERATOR_GT:  value.SetValueInteger((value.GetValueInteger() >  partialvalue.GetValueInteger())); break;
01645               case SCRIPT_LNG_G_DOUBLEOPERATOR_GE:  value.SetValueInteger((value.GetValueInteger() >= partialvalue.GetValueInteger())); break;
01646               case SCRIPT_LNG_G_DOUBLEOPERATOR_EQ:  value.SetValueInteger((value.GetValueInteger() == partialvalue.GetValueInteger())); break;
01647               case SCRIPT_LNG_G_DOUBLEOPERATOR_NE:  value.SetValueInteger((value.GetValueInteger() != partialvalue.GetValueInteger())); break;
01648             }
01649         }
01650     }
01651 }
01652 
01653 
01665 void SCRIPT_LNG_G::EvalExp2(SCRIPT_LNG_G_VAR& value)
01666 {
01667   XCHAR   operation;
01668   XSTRING okops;
01669 
01670   okops.Format(__L("(%c%c-+"), SCRIPT_LNG_G_DOUBLEOPERATOR_INC, SCRIPT_LNG_G_DOUBLEOPERATOR_DEC);
01671 
01672   EvalExp3(value);
01673 
01674   while(((operation = currenttoken[0]) == __C('+')) || (operation == __C('-')))
01675     {
01676       GetToken();
01677 
01678       if((tokentype == SCRIPT_LNG_G_TOKENTYPES_DELIMITER) && (okops.FindCharacter(currenttoken[0]) == XSTRING_NOTFOUND))
01679         {
01680           HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
01681           return;
01682         }
01683 
01684       if(tokentype == SCRIPT_LNG_G_TOKENTYPES_STRING)
01685         {
01686           HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
01687           return;
01688         }
01689 
01690       if(tokentype == SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER)
01691         {
01692           SCRIPT_LNG_G_VAR* var = IsVariable(currenttoken);
01693           if(var)
01694             {
01695               if(var->GetType()==SCRIPT_LNG_G_TOKENIREPS_STRING)
01696                 {
01697                   HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
01698                   return;
01699                 }
01700             }
01701         }
01702 
01703       SCRIPT_LNG_G_VAR partialvalue;
01704 
01705       EvalExp3(partialvalue);
01706 
01707       switch(operation)
01708         {
01709           case __C('-'):    value.SetValueInteger((value.GetValueInteger() - partialvalue.GetValueInteger()));    break;
01710           case __C('+'):    value.SetValueInteger((value.GetValueInteger() + partialvalue.GetValueInteger()));    break;
01711         }
01712     }
01713 }
01714 
01715 
01727 void SCRIPT_LNG_G::EvalExp3(SCRIPT_LNG_G_VAR& value)
01728 {
01729   XCHAR         operation;
01730   XSTRING   okops;
01731   SCRIPT_LNG_G_VAR  partialvalue;
01732 
01733   okops.Format(__L("(%c%c-+"), SCRIPT_LNG_G_DOUBLEOPERATOR_INC, SCRIPT_LNG_G_DOUBLEOPERATOR_DEC);
01734 
01735   EvalExp4(value);
01736 
01737   while( ((operation = currenttoken[0]) == __C('*')) || (operation == __C('/')) || (operation == __C('%')))
01738     {
01739       GetToken();
01740 
01741       if((tokentype == SCRIPT_LNG_G_TOKENTYPES_DELIMITER) && (okops.FindCharacter(currenttoken[0]) == XSTRING_NOTFOUND))
01742         {
01743           HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
01744           return;
01745         }
01746 
01747       EvalExp4(partialvalue);
01748 
01749       switch(operation)
01750         {
01751           // mul, div, or modulus
01752           case __C('*'): value.SetValueInteger(value.GetValueInteger() * partialvalue.GetValueInteger());
01753                          break;
01754 
01755           case __C('/'): if(partialvalue.GetValueInteger() == 0)
01756                            {
01757                              HaveError(SCRIPT_LNG_G_ERRORCODE_DIV_BY_ZERO);
01758                              return;
01759                            }
01760                          value.SetValueInteger((value.GetValueInteger() / partialvalue.GetValueInteger()));
01761                          break;
01762 
01763           case __C('%'): { int t = (value.GetValueInteger() / partialvalue.GetValueInteger());
01764                            value.SetValueInteger(value .GetValueInteger() - (t * partialvalue.GetValueInteger()));
01765                          }
01766                          break;
01767         }
01768     }
01769 }
01770 
01771 
01783 void SCRIPT_LNG_G::EvalExp4(SCRIPT_LNG_G_VAR& value)
01784 {
01785   XCHAR operation;
01786   XCHAR temptoken;
01787 
01788   operation = __C('\0');
01789 
01790   if(tokentype != SCRIPT_LNG_G_TOKENTYPES_STRING)
01791     {
01792       if((currenttoken[0] == __C('+'))  ||
01793          (currenttoken[0] == __C('-'))  ||
01794          (currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_INC) ||
01795          (currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_DEC))
01796         {
01797           temptoken = currenttoken[0];
01798           operation = currenttoken[0];
01799 
01800           GetToken();
01801 
01802           SCRIPT_LNG_G_VAR* var = FindVariable(currenttoken);
01803           if(var)
01804             {
01805               if(temptoken == SCRIPT_LNG_G_DOUBLEOPERATOR_INC) var->SetValueInteger(var->GetValueInteger()+1);
01806               if(temptoken == SCRIPT_LNG_G_DOUBLEOPERATOR_DEC) var->SetValueInteger(var->GetValueInteger()-1);
01807 
01808               AssignVariable(currenttoken,(*var));
01809             }
01810         }
01811     }
01812 
01813   EvalExp5(value);
01814 
01815   if(operation == __C('-'))  value.SetValueInteger(-value.GetValueInteger());
01816 }
01817 
01818 
01830 void SCRIPT_LNG_G::EvalExp5(SCRIPT_LNG_G_VAR& value)
01831 {
01832   if(currenttoken[0] == __C('('))
01833     {
01834       GetToken();
01835 
01836       EvalExp0(value);
01837 
01838       if(currenttoken[0] != __C(')'))
01839         {
01840           HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
01841           return;
01842         }
01843 
01844       GetToken();
01845 
01846     } else Atom(value);
01847 }
01848 
01849 
01861 void SCRIPT_LNG_G::Atom(SCRIPT_LNG_G_VAR& value)
01862 {
01863   switch(tokentype) 
01864     {
01865       case SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER : { SCRIPT_LIB_FUNCTION* function = GetLibraryFunction(currenttoken);
01866                                                   if(function)
01867                                                     {
01868                                                       if(function->GetFunctionLibrary())
01869                                                         {
01870                                                           XVECTOR<XVARIANT*>  funcparams;
01871                                                           XVARIANT            funcreturnvalue;
01872                                                           SCRIPT_LNG_G_VAR    params[SCRIPT_LNG_G_NUMPARAMS];
01873                                                        
01874                                                           int nparams = GetFuncParams(params);                                                          
01875                                                           for(int c=0; c<nparams; c++)
01876                                                             {
01877                                                               XVARIANT* variant = new XVARIANT();
01878                                                               if(variant)
01879                                                                 {
01880                                                                   params[c].ConvertToXVariant((*variant));
01881                                                                   funcparams.Add(variant);
01882                                                                 }
01883                                                             }
01884                                                           
01885                                                           (*function->GetFunctionLibrary())(function->GetLibrary(), this, &funcparams, &funcreturnvalue);
01886 
01887                                                           value.ConvertFromXVariant(funcreturnvalue);
01888                                                           value.SetIsReturnValue(true);
01889 
01890                                                           funcparams.DeleteContents();
01891                                                           funcparams.DeleteAll();
01892                                                         }
01893                                                        else
01894                                                         {
01895                                                           HaveError(SCRIPT_LNG_G_ERRORCODE_FUNC_UNDEF);
01896                                                           return;
01897                                                         }
01898                                                     }
01899                                                   else
01900                                                     {
01901                                                       if(FindFunction(currenttoken))
01902                                                         {
01903                                                           XCHAR tempcurrentfunc[_MAXSTR];
01904 
01905                                                           XSTRING::Set(tempcurrentfunc,currentfunction);
01906 
01907                                                           Call();
01908 
01909                                                           value.Set(&returnvalue);
01910                                                           value.SetIsReturnValue(true);
01911 
01912                                                           XSTRING::Set(currentfunction,tempcurrentfunc);
01913 
01914                                                           returnvalue.SetType(SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK);
01915                                                           returnvalue.SetValueInteger(0);
01916 
01917 
01918                                                         }
01919                                                       else
01920                                                         {
01921                                                           XCHAR tempcurrenttoken[_MAXSTR];
01922 
01923                                                           SCRIPT_LNG_G_VAR* var = FindVariable(currenttoken);
01924                                                           if(var)
01925                                                             {
01926                                                               value.Set(var);
01927 
01928                                                               XSTRING::Set(tempcurrenttoken,currenttoken);
01929 
01930                                                               GetToken();
01931 
01932                                                               if((currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_INC) || (currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_DEC))
01933                                                                 {
01934                                                                   SCRIPT_LNG_G_VAR* var = FindVariable(tempcurrenttoken);
01935                                                                   if(var)
01936                                                                     {
01937                                                                       if(currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_INC)
01938                                                                               var->SetValueInteger(var->GetValueInteger()+1);
01939                                                                         else  var->SetValueInteger(var->GetValueInteger()-1);
01940 
01941                                                                       AssignVariable(tempcurrenttoken, (*var));
01942                                                                     }
01943 
01944                                                                 } else PutBackToken();
01945                                                             }
01946                                                         }
01947                                                     }
01948 
01949                                                   GetToken();
01950                                                 }
01951                                                 return;
01952 
01953       case SCRIPT_LNG_G_TOKENTYPES_NUMBER     : { XSTRING string(currenttoken);
01954 
01955                                                   value.SetType(SCRIPT_LNG_G_TOKENIREPS_INT);
01956                                                   value.SetValueInteger(string.ConvertToInt());
01957                                                   GetToken();
01958                                                 }
01959                                                 return;
01960 
01961       case SCRIPT_LNG_G_TOKENTYPES_STRING     : { XSTRING* string= new XSTRING();;
01962                                                   if(string)  
01963                                                     {  
01964                                                       (*string) = currenttoken;        
01965 
01966                                                       value.SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);  
01967                                                       value.SetHaveReservedSize(true);
01968                                                       value.SetValueString(string);
01969                                                     }
01970 
01971 
01972                                                   /*    
01973                                                   if(value.GetValueString())
01974                                                     {
01975                                                       (*value.GetValueString()) = currenttoken;
01976                                                     }
01977                                                    else
01978                                                     {
01979                                                       string = new XSTRING();
01980 
01981                                                       if(string) 
01982                                                       value.SetValueString(string);
01983 
01984                                                       SCRIPT_LNG_G_VAR* vt = new SCRIPT_LNG_G_VAR();
01985                                                       if(vt)
01986                                                         {
01987                                                           vt->SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
01988                                                           vt->SetValueString(string);
01989 
01990                                                           localvarsstack.Add(vt);
01991                                                         }
01992                                                     }
01993                                                   */
01994 
01995                                                   GetToken();
01996                                                 }
01997                                                 return;
01998 
01999       case SCRIPT_LNG_G_TOKENTYPES_DELIMITER  : if(currenttoken[0] == __C('\''))
02000                                                   {
02001                                                     value.SetValueCharacter((XCHAR)(*ipprg));
02002                                                     ipprg++;
02003 
02004                                                     if((*ipprg)!=__C('\''))
02005                                                       {
02006                                                         HaveError(SCRIPT_LNG_G_ERRORCODE_QUOTE_EXPECTED);
02007                                                         return;
02008                                                       }
02009 
02010                                                     ipprg++;
02011 
02012                                                     GetToken();
02013 
02014                                                     return;
02015                                                   }
02016 
02017                                                 if(currenttoken[0] == __C(')'))
02018                                                   {
02019                                                   }
02020                                                  else HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
02021 
02022                                                 return;
02023 
02024                         default               : HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
02025                                                 return;
02026 
02027     }
02028 }
