02030 
02042 SCRIPT_LNG_G_VAR* SCRIPT_LNG_G::IsVariable(XCHAR* variablename)
02043 {
02044   if(!localvarsstack.IsEmpty())
02045     {
02046       for(int c=((int)localvarsstack.GetSize()-1); c>= functioncallstack.GetLast(); c--)
02047         {
02048           SCRIPT_LNG_G_VAR* var = localvarsstack.Get(c);
02049           if(var)
02050             {
02051               if(!var->GetName()->Compare(variablename)) return var;
02052             }
02053         }
02054     }
02055 
02056 
02057   for(XDWORD c=0; c<(int)globalvars.GetSize(); c++)
02058     {
02059       SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)globalvars.Get(c);
02060       if(var)
02061         {
02062           if(!var->GetName()->Compare(variablename)) return var;
02063         }
02064     }
02065 
02066   return NULL;
02067 }
02068 
02069 
02082 void SCRIPT_LNG_G::AssignVariable(XCHAR* variablename, SCRIPT_LNG_G_VAR& value)
02083 {
02084   if(!localvarsstack.IsEmpty())
02085     {
02086       for(int c=localvarsstack.GetSize()-1; c>=functioncallstack.GetLast(); c--)
02087         {
02088           SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)localvarsstack.Get(c);
02089           if(var)
02090             {
02091               if(!var->GetName()->Compare(variablename))
02092                 {
02093                   if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_CHAR)
02094                     {
02095                       var->SetValueCharacter(value.GetValueCharacter());
02096                     }
02097                    else
02098                     {
02099                       if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_INT)
02100                         {
02101                           var->SetValueInteger(value.GetValueInteger());
02102                         }
02103                        else
02104                         {
02105                           if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_STRING)
02106                             {
02107                               XSTRING* string1 = var->GetValueString();
02108                               XSTRING* string2 = value.GetValueString();
02109 
02110                               if(string1 && string2)
02111                                 {
02112                                   if(string1 != string2)
02113                                     {
02114                                       XSTRING* newstring = new XSTRING();
02115                                       if(newstring)
02116                                         {
02117                                           delete string1;
02118                                           (*newstring) = string2->Get();
02119 
02120                                           var->SetValueString(newstring);
02121                                           var->SetHaveReservedSize(true);
02122                                         }
02123                                     }
02124                                 }
02125                             }
02126                         }
02127                     }
02128 
02129                   return;
02130                 }
02131             }
02132         }
02133     }
02134 
02135   for(XDWORD c=0;c<globalvars.GetSize();c++)
02136     {
02137       SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)globalvars.Get(c);
02138       if(var)
02139         {
02140           if(!var->GetName()->Compare(variablename))
02141             {
02142               if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_CHAR)
02143                 {
02144                   var->SetValueCharacter(value.GetValueCharacter());
02145                 }
02146                else
02147                 {
02148                   if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_INT)
02149                     {
02150                       var->SetValueInteger(value.GetValueInteger());
02151                     }
02152                    else
02153                     {
02154                       if(var->GetType() == SCRIPT_LNG_G_TOKENIREPS_STRING)
02155                         {
02156                           XSTRING* string1 = var->GetValueString();
02157                           XSTRING* string2 = value.GetValueString();
02158 
02159                           if(string1 && string2)
02160                             {
02161                               if(string1 != string2)
02162                                 {
02163                                   XSTRING* newstring = new XSTRING();
02164                                   if(newstring)
02165                                     {
02166                                       delete string1;
02167                                       (*newstring) = string2->Get();
02168 
02169                                       var->SetValueString(newstring);
02170                                       var->SetHaveReservedSize(true);
02171                                     }
02172                                 }
02173                             }
02174                         }
02175                     }
02176                 }
02177 
02178               return;
02179             }
02180         }
02181     }
02182 
02183   HaveError(SCRIPT_LNG_G_ERRORCODE_NOT_VAR);
02184 }
02185 
02186 
02198 SCRIPT_LNG_G_VAR* SCRIPT_LNG_G::FindVariable(XCHAR* variablename)
02199 {
02200   if(!localvarsstack.IsEmpty())
02201     {
02202       for(int c=localvarsstack.GetSize()-1; c>=functioncallstack.GetLast(); c--)
02203         {
02204           SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)localvarsstack.Get(c);
02205           if(var)
02206             {
02207               if(!var->GetName()->Compare(variablename)) return var;
02208             }
02209         }
02210     }
02211 
02212   for(int c=0; c<(int)globalvars.GetSize(); c++)
02213     {
02214       SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)globalvars.Get(c);
02215       if(var)
02216         {
02217           if(!var->GetName()->Compare(variablename)) return var;
02218         }
02219     }
02220 
02221   HaveError(SCRIPT_LNG_G_ERRORCODE_NOT_VAR);
02222 
02223   return NULL;
02224 }
02225 
02226 
02238 SCRIPT_LNG_G_TOKENIREPS SCRIPT_LNG_G::FindVariableType(XCHAR* variablename)
02239 {
02240   if(!localvarsstack.IsEmpty())
02241     {
02242       for(int c=localvarsstack.GetSize()-1; c>=functioncallstack.GetLast(); c--)
02243         {
02244           SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)localvarsstack.Get(c);
02245           if(var)
02246             {
02247               if(!var->GetName()->Compare(variablename))  return var->GetType();
02248             }
02249         }
02250     }
02251 
02252   for(int c=0; c<(int)globalvars.GetSize(); c++)
02253     {
02254       SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)globalvars.Get(c);
02255       if(var)
02256         {
02257           if(!var->GetName()->Compare(variablename)) return var->GetType();
02258         }
02259     }
02260 
02261   return SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK;
02262 }
02263 
02264 
02276 XCHAR* SCRIPT_LNG_G::FindFunction(XCHAR* name)
02277 {
02278   for(XDWORD c=0; c<functiontable.GetSize(); c++)
02279     {
02280       SCRIPT_LNG_G_FUNCTIONTYPE* functiontype = (SCRIPT_LNG_G_FUNCTIONTYPE*)functiontable.Get(c);
02281       if(functiontype)
02282         {
02283           if(!functiontype->GetName()->Compare(name))
02284             {
02285               return functiontype->GetLocation();
02286             }
02287         }
02288     }
02289 
02290   return NULL;
02291 }
02292 
02293 
02305 SCRIPT_LNG_G_FUNCTIONTYPE* SCRIPT_LNG_G::GetFunction(XCHAR* name)
02306 {
02307   for(XDWORD c=0; c<functiontable.GetSize(); c++)
02308     {
02309       SCRIPT_LNG_G_FUNCTIONTYPE* functiontype = (SCRIPT_LNG_G_FUNCTIONTYPE*)functiontable.Get(c);
02310       if(functiontype)
02311         {
02312           if(!functiontype->GetName()->Compare(name))
02313             {
02314               return functiontype;
02315             }
02316         }
02317     }
02318 
02319   return NULL;
02320 }
02321 
02322 
02332 void SCRIPT_LNG_G::FindEndofBlock()
02333 {
02334   int brace;
02335 
02336   GetToken();
02337 
02338   if(currenttoken[0] != __C('{'))
02339     {
02340       HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
02341       return;
02342     }
02343 
02344   brace = 1;
02345 
02346   do{ GetToken();
02347 
02348       if(currenttoken[0] == __C('{'))
02349         {
02350           brace++;
02351         }
02352        else
02353         {
02354           if(currenttoken[0] == __C('}')) brace--;
02355         }
02356 
02357     } while(brace && tokenireps != SCRIPT_LNG_G_TOKENIREPS_END);
02358 
02359   if(tokenireps==SCRIPT_LNG_G_TOKENIREPS_END)
02360     {
02361       HaveError(SCRIPT_LNG_G_ERRORCODE_UNBAL_BRACES);
02362     }
02363 }
02364 
02365 
02375 void SCRIPT_LNG_G::DeclareGlobalVariable()
02376 {
02377   SCRIPT_LNG_G_TOKENIREPS vartype;
02378   SCRIPT_LNG_G_VAR*       vt;
02379 
02380   GetToken();
02381 
02382   vartype = tokenireps;
02383 
02384   do{ GetToken();
02385 
02386       for(int c=0; c<(int)globalvars.GetSize(); c++)
02387         {
02388           SCRIPT_LNG_G_VAR* vttmp = (SCRIPT_LNG_G_VAR*)globalvars.Get(c);
02389 
02390           if(!vttmp->GetName()->Compare(currenttoken))
02391             {
02392               HaveError(SCRIPT_LNG_G_ERRORCODE_DUP_VAR);
02393               return;
02394             }
02395         }
02396 
02397       vt = new SCRIPT_LNG_G_VAR();
02398       if(!vt) return;
02399 
02400       vt->SetType(vartype);      
02401       vt->SetName(currenttoken);
02402       vt->SetHaveReservedSize(false);
02403                   
02404       if((vartype==SCRIPT_LNG_G_TOKENIREPS_STRING)&&(!vt->GetValueString()))
02405         {
02406           XSTRING* string = new XSTRING();
02407           if(string) 
02408             {
02409               vt->SetValueString(string);
02410               vt->SetHaveReservedSize(true);
02411             }
02412         } 
02413 
02414       globalvars.Add(vt);
02415 
02416       GetToken();
02417 
02418     } while(currenttoken[0] == __C(','));
02419 
02420   if(currenttoken[0] != __C(';'))  
02421     {
02422       HaveError(SCRIPT_LNG_G_ERRORCODE_SEMI_EXPECTED);
02423     }
02424 }
02425 
02426 
02436 void SCRIPT_LNG_G::DeclareLocalVariable()
02437 {
02438   SCRIPT_LNG_G_TOKENIREPS vartype;
02439   SCRIPT_LNG_G_VAR*       vt;
02440 
02441   GetToken();
02442 
02443   vartype = tokenireps;
02444 
02445   do{ GetToken();
02446 
02447       if(!localvarsstack.IsEmpty())
02448         {
02449           for(int c=localvarsstack.GetSize()-1; c>=nestscopestack.GetLast(); c--)
02450             {
02451               SCRIPT_LNG_G_VAR* var = (SCRIPT_LNG_G_VAR*)localvarsstack.Get(c);
02452 
02453               if(var)
02454                 {
02455                   if(!var->GetName()->Compare(currenttoken))
02456                     {
02457                       HaveError(SCRIPT_LNG_G_ERRORCODE_DUP_VAR);
02458                       return;
02459                     }
02460                 }
02461             }
02462           }
02463 
02464       vt = new SCRIPT_LNG_G_VAR();
02465       if(!vt) 
02466         {
02467           return;
02468         }
02469 
02470       vt->SetType(vartype);        
02471       vt->SetName(currenttoken);
02472       vt->SetHaveReservedSize(false);
02473 
02474       if((vartype==SCRIPT_LNG_G_TOKENIREPS_STRING)&&(!vt->GetValueString()))
02475         {
02476           XSTRING* string = new XSTRING();
02477           if(string) 
02478             {
02479               vt->SetValueString(string);
02480               vt->SetHaveReservedSize(true);
02481             }
02482         }
02483 
02484       localvarsstack.Add(vt);
02485 
02486       GetToken();
02487 
02488     } while(currenttoken[0] == __C(','));
02489 
02490   if(currenttoken[0] != __C(';'))
02491     {
02492       HaveError(SCRIPT_LNG_G_ERRORCODE_SEMI_EXPECTED);      
02493     }
02494 }
02495 
02496 
02506 SCRIPT_LNG_G_TOKENTYPES SCRIPT_LNG_G::GetToken()
02507 {
02508   XCHAR*    temptoken;   
02509   XSTRING   dobleops;
02510   XSTRING   delimiters;  
02511   XSTRING   otherdelimiters;
02512 
02513   dobleops        = __L("!<>=+-");
02514   delimiters      = __L("{}");  
02515   otherdelimiters = __L("+-*^/%=;:(),'");
02516 
02517   tokentype       = SCRIPT_LNG_G_TOKENTYPES_UNDEFTT;
02518   tokenireps      = SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK;
02519 
02520   temptoken       = currenttoken;
02521   (*temptoken)    = __C('\0');
02522 
02523   // Skip over white space.
02524   while(IsSpace(*ipprg) && (*ipprg))
02525     {
02526       ++ipprg;
02527     }
02528 
02529   // Skip over newline.
02530   while((*ipprg) == __C('\r'))
02531     {
02532       ++ipprg;
02533       ++ipprg;
02534 
02535       // Again, skip over white space.
02536       while(IsSpace(*ipprg) && (*ipprg))
02537         {
02538           ++ipprg;
02539         }
02540     }
02541 
02542   // Check for end of ipprgram.
02543   if((*ipprg) == __C('\0'))
02544     {
02545       currenttoken[0]   = __C('\0');
02546       tokenireps        = SCRIPT_LNG_G_TOKENIREPS_END;
02547 
02548       return (tokentype = SCRIPT_LNG_G_TOKENTYPES_DELIMITER);
02549     }
02550 
02551   // Check for block delimiters.
02552   if(delimiters.FindCharacter(*ipprg)!=XSTRING_NOTFOUND)
02553     {
02554       (*temptoken) = (*ipprg);
02555       temptoken++;
02556 
02557       (*temptoken) = __C('\0');
02558       ipprg++;
02559 
02560       return (tokentype = SCRIPT_LNG_G_TOKENTYPES_BLOCK);
02561     }
02562 
02563   // Look for comments.
02564   if((*ipprg) == __C('/'))
02565     {
02566       if(*(ipprg + 1) == __C('*'))
02567         { // is a /* comment
02568           ipprg += 2;
02569 
02570           do{ // find end of comment
02571               while((*ipprg) != __C('*')) 
02572                 {
02573                   ipprg++;
02574                 }
02575               ipprg++;
02576 
02577             } while ((*ipprg) != __C('/'));
02578 
02579           ipprg++;
02580 
02581           return (tokentype = SCRIPT_LNG_G_TOKENTYPES_DELIMITER);
02582         }
02583        else
02584         {
02585           if(*(ipprg+1) == '/')
02586             {
02587               // is a // comment
02588               ipprg += 2;
02589               // Find end of comment.
02590               while(((*ipprg) != __C('\r')) && ((*ipprg) != __C('\0'))) 
02591                 {
02592                   ipprg++;
02593                 }
02594 
02595               if((*ipprg) == __C('\r')) 
02596                 {
02597                   ipprg +=2;
02598                 }
02599 
02600               return (tokentype = SCRIPT_LNG_G_TOKENTYPES_DELIMITER);
02601             }
02602         }
02603     }
02604 
02605   // Check for double-ops.
02606   if(dobleops.FindCharacter((*ipprg))!=XSTRING_NOTFOUND)
02607     {
02608       switch((*ipprg))
02609         {
02610           case '=': if(*(ipprg+1) == __C('='))
02611                       {
02612                         ipprg++; 
02613                         ipprg++;
02614 
02615                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_EQ;
02616                         temptoken++;
02617 
02618                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_EQ;
02619                         temptoken++;
02620 
02621                         (*temptoken) = __C('\0');
02622                       }
02623                     break;
02624 
02625           case '!': if(*(ipprg+1) == __C('='))
02626                       {
02627                         ipprg++; 
02628                         ipprg++;
02629 
02630                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_NE;
02631                         temptoken++;
02632 
02633                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_NE;
02634                         temptoken++;
02635 
02636                         (*temptoken) = __C('\0');
02637                       }
02638                     break;
02639 
02640           case '<': if(*(ipprg+1) == __C('='))
02641                       {
02642                         ipprg++; 
02643                         ipprg++;
02644 
02645                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_LE;
02646                         temptoken++;
02647 
02648                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_LE;
02649                       }
02650                      else
02651                       {
02652                         if(*(ipprg+1) == __C('<'))
02653                           {
02654                             ipprg++; 
02655                             ipprg++;
02656 
02657                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_LS;
02658                             temptoken++;
02659 
02660                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_LS;
02661                           }
02662                          else
02663                           {
02664                             ipprg++;
02665                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_LT;
02666                           }
02667                       }
02668 
02669                     temptoken++;
02670                     *temptoken = __C('\0');
02671                     break;
02672 
02673           case '>': if(*(ipprg+1) == __C('='))
02674                       {
02675                         ipprg++; 
02676                         ipprg++;
02677 
02678                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_GE;
02679                         temptoken++;
02680 
02681                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_GE;
02682                       }
02683                      else
02684                       {
02685                         if(*(ipprg+1) == __C('>'))
02686                           {
02687                             ipprg++; 
02688                             ipprg++;
02689 
02690                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_RS;
02691                             temptoken++;
02692 
02693                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_RS;
02694                           }
02695                           else
02696                           {
02697                             ipprg++;
02698                             (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_GT;
02699                           }
02700                       }
02701                      temptoken++;
02702                      (*temptoken) = __C('\0');
02703                     break;
02704 
02705           case '+': if(*(ipprg+1) == __C('+'))
02706                       {
02707                         ipprg++;
02708                         ipprg++;
02709 
02710                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_INC;
02711                         temptoken++;
02712 
02713                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_INC;
02714                         temptoken++;
02715 
02716                         (*temptoken) = __C('\0');
02717                       }
02718                     break;
02719 
02720           case '-': if(*(ipprg+1) == __C('-'))
02721                       {
02722                         ipprg++;
02723                         ipprg++;
02724 
02725                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_DEC;
02726                         temptoken++;
02727 
02728                         (*temptoken) = SCRIPT_LNG_G_DOUBLEOPERATOR_DEC;
02729                         temptoken++;
02730 
02731                         (*temptoken) = '\0';
02732                       }
02733                     break;
02734         }
02735 
02736       if(currenttoken[0]) return(tokentype = SCRIPT_LNG_G_TOKENTYPES_DELIMITER);
02737     }
02738 
02739   // Check for other delimiters.
02740   if(otherdelimiters.FindCharacter((*ipprg))!=XSTRING_NOTFOUND)
02741     {
02742       (*temptoken) = (*ipprg);
02743       ipprg++;
02744       temptoken++;
02745       (*temptoken) = '\0';
02746 
02747       return (tokentype = SCRIPT_LNG_G_TOKENTYPES_DELIMITER);
02748     }
02749 
02750   // Read a quoted string.
02751   if((*ipprg) == __C('"'))
02752     {
02753       ipprg++;
02754 
02755       while(((*ipprg) != __C('"')) && ((*ipprg)!=__C('\r')) && (*ipprg))
02756         {
02757           // Check for \r escape sequence.
02758           if((*ipprg) == __C('\\'))
02759             {
02760               if(*(ipprg+1) == __C('n'))
02761                 {
02762                   ipprg++;
02763                   (*temptoken++) = __C('\n');
02764                 }
02765                else
02766                 {
02767                   if(*(ipprg+1) == __C('r'))
02768                     {
02769                       ipprg++;
02770                       (*temptoken++) = __C('\r');
02771                     }
02772                    else
02773                     {
02774                       if(*(ipprg+1) == '\\')
02775                         {
02776                           ipprg++;
02777                           (*temptoken++) = __C('\\');
02778                         }
02779                     }
02780                 }
02781             }
02782            else
02783             {
02784               if((temptoken - currenttoken) < SCRIPT_LNG_G_MAXTOKENLEN)  (*temptoken++) = (*ipprg);
02785             }
02786 
02787           ipprg++;
02788         }
02789 
02790         if((*ipprg) == __C('\r') || (*ipprg) == 0)
02791           {
02792             HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
02793             return tokentype;
02794           }
02795 
02796       ipprg++;
02797       (*temptoken) = '\0';
02798 
02799       return (tokentype = SCRIPT_LNG_G_TOKENTYPES_STRING);
02800     }
02801 
02802   // Read an integer number.
02803   if(IsDigit((*ipprg)))
02804     {
02805       while(!IsDelimiter((*ipprg)))
02806         {
02807           if((temptoken - currenttoken) < SCRIPT_LNG_G_MAXIDLEN)  (*temptoken++) = (*ipprg);
02808           ipprg++;
02809         }
02810       (*temptoken) = __C('\0');
02811 
02812       return (tokentype = SCRIPT_LNG_G_TOKENTYPES_NUMBER);
02813     }
02814 
02815   // Read identifier or keyword.
02816   if(IsAlpha((*ipprg)))
02817     {
02818       while(!IsDelimiter((*ipprg)) && ((*ipprg)!=__C('{')) && ((*ipprg)!=__C('}')))
02819         {
02820           if((temptoken - currenttoken) < SCRIPT_LNG_G_MAXIDLEN) (*temptoken++) = (*ipprg);
02821           ipprg++;
02822         }
02823 
02824       tokentype = SCRIPT_LNG_G_TOKENTYPES_TEMP;
02825     }
02826 
02827   (*temptoken) = __C('\0');
02828 
02829   // Determine if token is a keyword or identifier.
02830   if(tokentype == SCRIPT_LNG_G_TOKENTYPES_TEMP)
02831     {
02832       tokenireps = LookUpToken(currenttoken); // convert to internal form
02833 
02834       if(tokenireps)
02835              tokentype = SCRIPT_LNG_G_TOKENTYPES_KEYWORD; // is a keyword
02836         else tokentype = SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER;
02837     }
02838 
02839   // Check for unidentified XCHARacter in file.
02840   if(tokentype == SCRIPT_LNG_G_TOKENTYPES_UNDEFTT)
02841     {
02842       HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
02843     }
02844 
02845   return tokentype;
02846 }
02847 
02848 
02858 void SCRIPT_LNG_G::PreScan()
02859 {
02860   XCHAR*                      position;
02861   XCHAR*                      tempposition;
02862   SCRIPT_LNG_G_TOKENIREPS     datatype;
02863   SCRIPT_LNG_G_FUNCTIONTYPE*  functiontype;
02864   XSTRING                     temptoken;
02865   int                         brace = 0;
02866 
02867   position = ipprg;
02868 
02869   do{ while(brace)
02870         {
02871           GetToken();
02872 
02873           if((iscancelexec) || (errorcode != SCRIPT_ERRORCODE_NONE))
02874             {
02875               break;
02876             }
02877           
02878           if(tokenireps == SCRIPT_LNG_G_TOKENIREPS_END)
02879             {
02880               HaveError(SCRIPT_LNG_G_ERRORCODE_UNBAL_BRACES);
02881               return;
02882             }
02883 
02884           if(currenttoken[0] == __C('{')) brace++;
02885           if(currenttoken[0] == __C('}')) brace--;
02886         }
02887 
02888       if((iscancelexec) || (errorcode != SCRIPT_ERRORCODE_NONE))
02889         {
02890           break;
02891         }
02892 
02893       tempposition = ipprg;
02894 
02895       GetToken(); 
02896       
02897       if((iscancelexec) || (errorcode != SCRIPT_ERRORCODE_NONE))
02898         {
02899           break;
02900         }
02901 
02902       if(tokenireps == SCRIPT_LNG_G_TOKENIREPS_CHAR || tokenireps == SCRIPT_LNG_G_TOKENIREPS_INT || tokenireps == SCRIPT_LNG_G_TOKENIREPS_STRING)
02903         {
02904           datatype = tokenireps;
02905 
02906           GetToken();
02907           
02908           if(tokentype == SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER)
02909             {
02910               temptoken = currenttoken;
02911 
02912               GetToken();
02913               
02914               if((iscancelexec) || (errorcode != SCRIPT_ERRORCODE_NONE))
02915                 {
02916                   break;
02917                 }
02918 
02919               if(currenttoken[0] != __C('('))
02920                 {
02921                   ipprg = tempposition;
02922                   DeclareGlobalVariable();
02923                 }
02924                else
02925                 {
02926                   if(currenttoken[0] == __C('('))
02927                     {
02928                       functiontype = new SCRIPT_LNG_G_FUNCTIONTYPE();
02929                       if(!functiontype) return;
02930 
02931                       for(int c=0;c<(int)functiontable.GetSize();c++)
02932                         {
02933                           SCRIPT_LNG_G_FUNCTIONTYPE* functiontype = (SCRIPT_LNG_G_FUNCTIONTYPE*)functiontable.Get(c);
02934 
02935                           if(!functiontype->GetName()->Compare(temptoken))
02936                             {
02937                               HaveError(SCRIPT_LNG_G_ERRORCODE_DUP_FUNC);
02938                               return;
02939                             }
02940                         }
02941 
02942                       functiontype->SetLocation(ipprg);
02943                       functiontype->SetReturnType(datatype);
02944                       functiontype->SetName(temptoken.Get());
02945 
02946                       functiontable.Add(functiontype);
02947 
02948                       do{ GetToken();
02949                           
02950                           if((iscancelexec) || (errorcode != SCRIPT_ERRORCODE_NONE))
02951                             {
02952                               break;
02953                             }
02954 
02955                         } while(currenttoken[0] != __C(')'));
02956 
02957                     } else PutBackToken();
02958                 }
02959             }
02960         }
02961        else
02962         {
02963           if(currenttoken[0] == __C('{')) brace++;
02964           if(currenttoken[0] == __C('}')) brace--;
02965         }
02966 
02967       if(iscancelexec) break;
02968       if(errorcode != SCRIPT_ERRORCODE_NONE) break;   
02969 
02970     } while(tokenireps != SCRIPT_LNG_G_TOKENIREPS_END);
02971 
02972   if((brace) && (errorcode == SCRIPT_ERRORCODE_NONE))
02973     {
02974       HaveError(SCRIPT_LNG_G_ERRORCODE_UNBAL_BRACES);
02975       return;
02976     }
02977 
02978   ipprg = position;
02979 }
02980 
02981 
02991 void SCRIPT_LNG_G::GetArgs()
02992 {
02993   SCRIPT_LNG_G_VAR* values[SCRIPT_LNG_G_NUMPARAMS];
02994   SCRIPT_LNG_G_VAR* value = NULL;
02995   int        count;
02996 
02997   for(int c=0;c<SCRIPT_LNG_G_NUMPARAMS;c++)
02998     {
02999       values[c] = NULL;
03000     }
03001 
03002   count = 0;
03003 
03004   GetToken();
03005 
03006   if(*currenttoken != '(')
03007     {
03008       HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
03009       return;
03010     }
03011 
03012   // Process a comma-separated list of values.
03013   do{ value = new SCRIPT_LNG_G_VAR();
03014       if(!value) break;
03015 
03016       EvalExp((*value));
03017       values[count] = value; // save temporarily
03018 
03019       GetToken();
03020       count++;
03021 
03022     } while(*currenttoken == ',');
03023 
03024   count--;
03025 
03026   // Now, push on local_var_stack in reverse order.
03027   for(; count>=0; count--)
03028     {
03029       value = values[count];
03030       if(value)
03031         {
03032           value->SetIsArg(true);
03033 
03034           localvarsstack.Add(value);
03035         }
03036     }
03037 }
03038 
03039 
03049 void SCRIPT_LNG_G::GetParams()
03050 {
03051   SCRIPT_LNG_G_VAR* var;
03052   int        indexstack;
03053 
03054   indexstack = localvarsstack.GetSize()-1;
03055 
03056   // Process comma-separated list of parameters.
03057   do{
03058       GetToken();
03059 
03060       var = (SCRIPT_LNG_G_VAR*)localvarsstack.Get(indexstack);
03061 
03062       if(currenttoken[0] != __C(')') )
03063         {
03064           if(tokenireps != SCRIPT_LNG_G_TOKENIREPS_INT   &&
03065              tokenireps != SCRIPT_LNG_G_TOKENIREPS_CHAR  &&
03066              tokenireps != SCRIPT_LNG_G_TOKENIREPS_STRING)
03067           {
03068             HaveError(SCRIPT_LNG_G_ERRORCODE_TYPE_EXPECTED);
03069             return;
03070           }
03071 
03072           var->SetType(tokenireps);
03073           var->SetHaveReservedSize(false);
03074           if(tokenireps == SCRIPT_LNG_G_TOKENIREPS_STRING)
03075             {
03076               var->SetHaveReservedSize(true);            
03077             }
03078 
03079           GetToken();
03080 
03081           var->SetName(currenttoken);
03082           
03083           GetToken();
03084 
03085           indexstack--;
03086         }
03087        else break;
03088 
03089     } while(currenttoken[0] == __C(','));
03090 
03091   if(currenttoken[0] != __C(')'))
03092     {
03093       HaveError(SCRIPT_LNG_G_ERRORCODE_PAREN_EXPECTED);
03094     }
03095 }
03096 
03097 
03107 void SCRIPT_LNG_G::FunctionReturn()
03108 {
03109   SCRIPT_LNG_G_VAR value;
03110 
03111   value.Clear();
03112 
03113   SCRIPT_LNG_G_FUNCTIONTYPE* function = GetFunction(currentfunction);
03114 
03115   EvalExp(value);
03116 
03117   value.SetHaveReservedSize(false);
03118 
03119   if(!function)
03120     {
03121       return;
03122     }
03123   
03124   if(function->GetReturnType() == SCRIPT_LNG_G_TOKENIREPS_STRING)
03125     {
03126       returnvalue.SetType(SCRIPT_LNG_G_TOKENIREPS_STRING);
03127       returnvalue.SetValueString(value.GetValueString());
03128       returnvalue.SetHaveReservedSize(true);
03129     }
03130    else
03131     {
03132       returnvalue.SetType(SCRIPT_LNG_G_TOKENIREPS_INT);
03133       returnvalue.SetValueInteger(value.GetValueInteger());
03134     } 
03135 }
03136 
03137 
03147 void SCRIPT_LNG_G::Exec_IF()
03148 {
03149   SCRIPT_LNG_G_VAR cond;
03150 
03151   cond.Clear();
03152 
03153   EvalExp(cond);
03154 
03155   if(cond.GetValueInteger())
03156     {
03157       if(currenttoken[0] != __C('{'))
03158         {
03159           HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03160           return;
03161         }
03162 
03163       Interpret();
03164     }
03165    else
03166     {
03167       FindEndofBlock(); // find start of next line
03168       GetToken();
03169 
03170       if(tokenireps != SCRIPT_LNG_G_TOKENIREPS_ELSE)
03171         {
03172           PutBackToken();
03173           return;
03174         }
03175 
03176       GetToken();
03177 
03178       if(currenttoken[0] != __C('{'))
03179         {
03180           HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03181           return;
03182         }
03183 
03184       PutBackToken();
03185 
03186       Interpret();
03187     }
03188 }
03189 
03190 
03200 void SCRIPT_LNG_G::Exec_SWITCH()
03201 {
03202   SCRIPT_LNG_G_VAR sval;
03203   SCRIPT_LNG_G_VAR cval;
03204   int       brace;
03205 
03206   EvalExp(sval);
03207 
03208   if(currenttoken[0] != __C('{'))
03209     {
03210       HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03211       return;
03212     }
03213 
03214   nestscopestack.Add(localvarsstack.GetSize());
03215 
03216   for(;;)
03217     {
03218       brace = 1;
03219 
03220       do { GetToken();
03221 
03222            if(currenttoken[0] == '{')
03223                    brace++;
03224               else if(currenttoken[0] == '}') brace--;
03225 
03226          } while(tokenireps != SCRIPT_LNG_G_TOKENIREPS_CASE && tokenireps != SCRIPT_LNG_G_TOKENIREPS_END && brace);
03227 
03228       if(!brace) break;
03229 
03230       if(tokenireps == SCRIPT_LNG_G_TOKENIREPS_END)
03231         {
03232           HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
03233           return;
03234         }
03235 
03236       EvalExp(cval);
03237 
03238       GetToken();
03239 
03240       if(currenttoken[0] != __C(':'))
03241         {
03242           HaveError(SCRIPT_LNG_G_ERRORCODE_COLON_EXPECTED);
03243           return;
03244         }
03245 
03246       if(cval.GetValueInteger() == sval.GetValueInteger())
03247         {
03248           brace = 1;
03249 
03250           do{ Interpret();
03251 
03252               if(currenttoken[0] == __C('{'))
03253                     brace++;
03254                else if(currenttoken[0] == __C('}')) brace--;
03255 
03256             } while(!breakfound && tokenireps != SCRIPT_LNG_G_TOKENIREPS_END && brace);
03257 
03258           while(brace)
03259             {
03260               GetToken();
03261 
03262               if(currenttoken[0] == __C('{'))
03263                      brace++;
03264                 else if(currenttoken[0] == __C('}')) brace--;
03265             }
03266 
03267           breakfound = false;
03268 
03269           break;
03270         }
03271     }
03272 }
03273 
03274 
03284 void SCRIPT_LNG_G::Exec_WHILE()
03285 {
03286   SCRIPT_LNG_G_VAR cond;
03287   XCHAR*        templocation;
03288 
03289   PutBackToken();
03290 
03291   templocation = ipprg;
03292 
03293   GetToken();
03294 
03295   EvalExp(cond);
03296 
03297   if(currenttoken[0] != __C('{'))
03298     {
03299       HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03300       return;
03301     }
03302 
03303   if(cond.GetValueInteger())
03304     {
03305       Interpret();
03306     }
03307    else
03308     {
03309       FindEndofBlock();
03310       return;
03311     }
03312 
03313   ipprg = templocation;
03314 
03315   if(breakfound)
03316     {
03317       do { GetToken();
03318 
03319          } while((currenttoken[0] != __C('{')) && (tokenireps != SCRIPT_LNG_G_TOKENIREPS_END));
03320 
03321       PutBackToken();
03322 
03323       breakfound = false;
03324 
03325       FindEndofBlock();
03326 
03327       return;
03328     }
03329 }
03330 
03331 
03341 void SCRIPT_LNG_G::Exec_DO()
03342 {
03343   SCRIPT_LNG_G_VAR  cond;
03344   XCHAR*        templocation;
03345 
03346   PutBackToken();
03347 
03348   templocation = ipprg;
03349 
03350   GetToken();
03351   GetToken();
03352 
03353   if(currenttoken[0] != __C('{'))
03354     {
03355       HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03356       return;
03357     }
03358 
03359   PutBackToken();
03360 
03361   Interpret();
03362 
03363   if(breakfound)
03364     {
03365       ipprg = templocation;
03366 
03367       do{ GetToken();
03368 
03369         } while((currenttoken[0] != __C('{')) && (tokenireps != SCRIPT_LNG_G_TOKENIREPS_END));
03370 
03371       PutBackToken();
03372 
03373       FindEndofBlock();
03374 
03375       do{ GetToken();
03376 
03377         } while((currenttoken[0] != __C(';')) && (tokenireps != SCRIPT_LNG_G_TOKENIREPS_END));
03378 
03379       if(tokenireps == SCRIPT_LNG_G_TOKENIREPS_END)
03380         {
03381           HaveError(SCRIPT_LNG_G_ERRORCODE_SYNTAX);
03382           return;
03383         }
03384 
03385       breakfound = false;
03386       return;
03387     }
03388 
03389   GetToken();
03390 
03391   if(tokenireps != SCRIPT_LNG_G_TOKENIREPS_WHILE)
03392     {
03393       HaveError(SCRIPT_LNG_G_ERRORCODE_WHILE_EXPECTED);
03394       return;
03395     }
03396 
03397   EvalExp(cond);
03398 
03399   if(cond.GetValueInteger()) ipprg = templocation;
03400 }
03401 
03402 
03412 void SCRIPT_LNG_G::Exec_FOR()
03413 {
03414   SCRIPT_LNG_G_VAR cond;
03415   XCHAR*        templocation;
03416   XCHAR*        templocation2;
03417   int           parent;
03418 
03419   GetToken();
03420 
03421   EvalExp(cond);
03422 
03423   if(currenttoken[0] != __C(';'))
03424     {
03425       HaveError(SCRIPT_LNG_G_ERRORCODE_SEMI_EXPECTED);
03426       return;
03427     }
03428 
03429   ipprg++;
03430 
03431   templocation = ipprg;
03432 
03433   for(;;)
03434     {
03435 
03436       EvalExp(cond);
03437       if(currenttoken[0] != __C(';'))
03438         {
03439           HaveError(SCRIPT_LNG_G_ERRORCODE_SEMI_EXPECTED);
03440           return;
03441         }
03442 
03443       ipprg++;
03444       templocation2 = ipprg;
03445 
03446       parent = 1;
03447       while(parent)
03448         {
03449           GetToken();
03450 
03451           if(currenttoken[0] == __C('(')) parent++;
03452           if(currenttoken[0] == __C(')')) parent--;
03453         }
03454 
03455       GetToken();
03456 
03457       if(currenttoken[0] != __C('{'))
03458         {
03459           HaveError(SCRIPT_LNG_G_ERRORCODE_BRACE_EXPECTED);
03460           return;
03461         }
03462 
03463       PutBackToken();
03464 
03465       if(cond.GetValueInteger())
03466         {
03467           Interpret();
03468         }
03469        else
03470         {
03471           FindEndofBlock();
03472           return;
03473         }
03474 
03475       ipprg = templocation2;
03476 
03477       if(breakfound)
03478         {
03479           do{ GetToken();
03480 
03481             } while(currenttoken[0] != '{' && tokenireps!= SCRIPT_LNG_G_TOKENIREPS_END);
03482 
03483           PutBackToken();
03484 
03485           breakfound = false;
03486 
03487           FindEndofBlock();
03488           return;
03489         }
03490 
03491 
03492       EvalExp(cond);
03493 
03494       ipprg = templocation;
03495     }
03496 }
03497 
03498 
03499 
03509 bool SCRIPT_LNG_G::Interpret()
03510 {
03511   SCRIPT_LNG_G_VAR value;
03512   int          block = 0;
03513 
03514   do{ if(iscancelexec)
03515         {
03516           HaveError(SCRIPT_LNG_G_ERRORCODE_USERBREAK);
03517           return false;
03518         }
03519 
03520       if(errorcode!=SCRIPT_ERRORCODE_NONE) return false;
03521 
03522       if(breakfound)  return false;
03523       if(returnfound) return true;
03524 
03525       tokentype = GetToken();
03526 
03527       if(tokentype == SCRIPT_LNG_G_TOKENTYPES_IDENTIFIER || currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_INC || currenttoken[0] == SCRIPT_LNG_G_DOUBLEOPERATOR_DEC)
03528         {
03529           value.Clear();
03530 
03531           PutBackToken();
03532 
03533           EvalExp(value);
03534       
03535           if(currenttoken[0] != ';')
03536             {
03537               HaveError(SCRIPT_LNG_G_ERRORCODE_SEMI_EXPECTED);
03538               return false;
03539             }
03540         }
03541        else
03542          if(tokentype==SCRIPT_LNG_G_TOKENTYPES_BLOCK)
03543           {
03544             if(currenttoken[0] == '{')
03545               {
03546                 block = 1;
03547                 nestscopestack.Add(localvarsstack.GetSize());
03548               }
03549              else
03550               {
03551                 localvarsstack.ResizeContents(nestscopestack.GetLast());
03552                 nestscopestack.DeleteLast();
03553 
03554                 return false;
03555               }
03556           }
03557          else // is keyword
03558           {
03559             switch(tokenireps)
03560               {
03561                 case SCRIPT_LNG_G_TOKENIREPS_CHAR   :
03562                 case SCRIPT_LNG_G_TOKENIREPS_INT    :
03563                 case SCRIPT_LNG_G_TOKENIREPS_STRING : PutBackToken();
03564                                                       DeclareLocalVariable();
03565                                                       break;
03566 
03567                 case SCRIPT_LNG_G_TOKENIREPS_RETURN : FunctionReturn();
03568                                                       returnfound = true;
03569                                                       return true;
03570 
03571                 case SCRIPT_LNG_G_TOKENIREPS_IF     : Exec_IF();
03572                                                       break;
03573 
03574                 case SCRIPT_LNG_G_TOKENIREPS_ELSE   : FindEndofBlock();
03575                                                       break;
03576 
03577                 case SCRIPT_LNG_G_TOKENIREPS_WHILE  : Exec_WHILE();
03578                                                       break;
03579 
03580                 case SCRIPT_LNG_G_TOKENIREPS_DO     : Exec_DO();
03581                                                       break;
03582 
03583                 case SCRIPT_LNG_G_TOKENIREPS_FOR    : // process a for loop
03584                                                       Exec_FOR();
03585                                                       break;
03586 
03587                 case SCRIPT_LNG_G_TOKENIREPS_BREAK  : breakfound = true;
03588 
03589                                                       localvarsstack.ResizeContents(nestscopestack.GetLast());
03590                                                       nestscopestack.DeleteLast();
03591 
03592                                                       return false;
03593 
03594                 case SCRIPT_LNG_G_TOKENIREPS_SWITCH : Exec_SWITCH();
03595                                                       break;
03596 
03597                 case SCRIPT_LNG_G_TOKENIREPS_END    : break;
03598 
03599 
03600               }
03601           }
03602 
03603       if(returnfound) return true;
03604 
03605     } while (tokenireps != SCRIPT_LNG_G_TOKENIREPS_END && block);
03606 
03607   return false;
03608 }
03609 
03610 
03620 void SCRIPT_LNG_G::Call()
03621 {
03622   XCHAR* location;
03623   XCHAR* templocation;
03624   int    localvarstacksize;
03625 
03626   location = FindFunction(currenttoken);
03627   if(!location)
03628     {
03629       HaveError(SCRIPT_LNG_G_ERRORCODE_FUNC_UNDEF);
03630       return;
03631     }
03632   else
03633     {
03634       XSTRING::Set(currentfunction,currenttoken);
03635 
03636       localvarstacksize = localvarsstack.GetSize();
03637 
03638       GetArgs();
03639       templocation = ipprg;
03640 
03641       functioncallstack.Add(localvarstacksize);
03642 
03643       ipprg = location;
03644 
03645       GetParams();
03646 
03647       returnfound = false;
03648 
03649       Interpret();
03650 
03651       returnfound = false;
03652 
03653       ipprg = templocation;
03654 
03655       if(functioncallstack.IsEmpty())
03656         {
03657           HaveError(SCRIPT_LNG_G_ERRORCODE_RET_NOCALL);
03658           return;
03659         }
03660 
03661       localvarsstack.ResizeContents(functioncallstack.GetLast());
03662 
03663       functioncallstack.DeleteLast();
03664     }
03665 }
03666 
03667 
03679 void SCRIPT_LNG_G::ThreadFunction(void* data)
03680 {
03681   SCRIPT_LNG_G* script = (SCRIPT_LNG_G*)data;
03682   if(!script) return;
03683 
03684   int returnvaluescript;
03685 
03686   script->SetErrorScript(script->Run(&returnvaluescript));
03687 
03688   script->SetReturnValueScript(returnvaluescript);
03689 
03690   script->GetThread()->Run(false);
03691 }
03692 
03693 
03704 void SCRIPT_LNG_G::Clean()
03705 {
03706   returnvaluescript   = 0;
03707 
03708   ipprg               = NULL;
03709 
03710   iscancelexec        = false;
03711 
03712   tokentype           = SCRIPT_LNG_G_TOKENTYPES_UNDEFTT;
03713   tokenireps          = SCRIPT_LNG_G_TOKENIREPS_UNDEFTOK;
03714 
03715   returnfound         = false;
03716   breakfound          = false;
03717 
03718   int c;
03719 
03720   for(c=0;c<SCRIPT_LNG_G_MAXTOKENLEN+1;c++)
03721     {
03722       currenttoken[c] = 0;
03723     }
03724 
03725   for(c=0;c<_MAXSTR;c++)
03726     {
03727       currentfunction[c] = 0;
03728     }
03729 
03730   returnvalue.SetValueInteger(0);
03731 }
