00263 bool SCRIPT_LNG_JAVASCRIPT::HaveMainFunction()
00264 {
00265   XSTRING mainfunctionname;
00266   XSTRING script;
00267   int     start = 0;
00268   int     end   = 0;
00269   XSTRING line;
00270 
00271   script = GetScript()->Get();
00272   if(script.IsEmpty()) return false;
00273 
00274   mainfunctionname = SCRIPT_LNG_JAVASCRIPT_MAINFUNCTIONNAME;
00275 
00276   int index = GetScript()->Find(mainfunctionname, false);
00277   if(index == XSTRING_NOTFOUND) return false;
00278 
00279   for(start=index; start>0; start--)
00280     {
00281       if((script.Get()[start] == __C('\r')) || (script.Get()[start] == __C('\n')))
00282         {
00283           break;
00284         }
00285     }
00286 
00287   for(end=index; end<(int)script.GetSize() ; end++)
00288     {
00289       if(script.Get()[end] == __C(')'))
00290         {
00291           break;
00292         }
00293     }
00294 
00295   script.Copy(start, end+1, line);
00296 
00297   line.DeleteCharacter(__C('\n'));
00298   line.DeleteCharacter(__C('\r'));
00299   line.DeleteCharacter(__C(' '));
00300 
00301   XSTRING searchmask;
00302 
00303   searchmask.Format(__L("function%s()"), mainfunctionname.Get());
00304 
00305   if(!line.Compare(searchmask)) return true;
00306 
00307   return false;
00308 }
