00145 int SCRIPT_LNG_LUA::Run(int* returnval)
00146 {
00147   errorcode  = SCRIPT_ERRORCODE_NONE;
00148   iscancelexec = false;
00149 
00150   int     status = 0;
00151 
00152   if(HaveMainFunction())
00153     {
00154       //Exec to ajust the Stack in Lua with Main Function. lua_getglobal don´t work well without this.
00155       lua_pcall(state, 0, LUA_MULTRET, 0);
00156     }
00157 
00158   lua_getglobal(state, SCRIPT_LNG_LUA_MAINFUNCTIONNAME);
00159 
00160   status = lua_isfunction(state, lua_gettop(state));
00161   if(status)
00162     {
00163       status = lua_pcall(state, 0, 0, 0);
00164     }
00165    else
00166     {
00167       lua_setglobal(state, SCRIPT_LNG_LUA_MAINFUNCTIONNAME);
00168       status = lua_pcall(state, 0, LUA_MULTRET, 0);
00169     }
00170 
00171 
00172   if(status != LUA_OK)
00173     {
00174       switch(status)
00175         {
00176           case LUA_YIELD      : errorcode = SCRIPT_LNG_LUA_ERRORCODE_YIELD;          break;
00177           case LUA_ERRRUN     : errorcode = SCRIPT_LNG_LUA_ERRORCODE_RUN;            break;
00178           case LUA_ERRSYNTAX  : errorcode = SCRIPT_LNG_LUA_ERRORCODE_SYNTAX;         break;
00179           case LUA_ERRMEM     : errorcode = SCRIPT_LNG_LUA_ERRORCODE_MEM;            break;
00180           case LUA_ERRGCMM    : errorcode = SCRIPT_LNG_LUA_ERRORCODE_GCMM;           break;
00181           case LUA_ERRERR     : errorcode = SCRIPT_LNG_LUA_ERRORCODE_ERRERR;         break;
00182         }
00183 
00184       HaveError(errorcode);
00185 
00186       return errorcode;
00187     }
00188 
00189   return errorcode;
00190 }
