00232 bool SCRIPT_LNG_LUA::HaveError(int errorcode)
00233 {
00234   if(errorcode != SCRIPT_ERRORCODE_NONE)
00235     {
00236       static XCHAR* errorstr[]= { __L("None")                                             ,
00237                                   __L("Internal error")                                   ,
00238                                   __L("Syntax error")                                     ,
00239                                   __L("Insufficient parameters")                          ,
00240                                   __L("Thread not yield")                                 ,
00241                                   __L("runtime error")                                    ,
00242                                   __L("memory allocation error")                          ,
00243                                   __L("error while running a __gc metamethod")            ,
00244                                   __L("error while running the error handler function")   ,
00245                                 };
00246       XSTRING   currenttoken;
00247       XPATH     namefile;
00248       XSTRING   errorstring;
00249       int       nline        = 0;
00250 
00251       currenttoken = lua_tostring(state, -1);
00252 
00253       namefile.AdjustSize(_MAXSTR);
00254       errorstring.AdjustSize(_MAXSTR);
00255       currenttoken.UnFormat(__L("[string \"%s\"]:%d:%s"), namefile.Get(), &nline, errorstring.Get());
00256       namefile.AdjustSize();
00257       errorstring.AdjustSize();
00258 
00259       namescript.Set(namefile);
00260       namefile.SetOnlyNamefileExt();
00261 
00262       SCRIPT_XEVENT xevent(this, SCRIPT_XEVENT_TYPE_ERROR);
00263 
00264       xevent.SetError(errorcode);
00265       xevent.GetErrorText()->Set(errorstr[errorcode]);
00266       xevent.GetCurrentToken()->Set(currenttoken);
00267       xevent.SetNLine(nline);
00268 
00269       XTRACE_PRINTCOLOR(4, __L("Script [%s] ERROR %d: %s, line %d \"%s\"") , namefile.Get(), errorcode, errorstr[errorcode], nline, errorstring.Get());
00270 
00271       PostEvent(&xevent);
00272 
00273       this->errorcode  = errorcode;
00274       iscancelexec     = true;
00275     }
00276 
00277   return (errorcode == SCRIPT_ERRORCODE_NONE)?false:true;
00278 }
