00290 bool SCRIPT_LNG_LUA::HaveMainFunction()
00291 {
00292   XSTRING mainfunctionname;
00293   XSTRING script;
00294   int     start = 0;
00295   int     end   = 0;
00296   XSTRING line;
00297 
00298   script = GetScript()->Get();
00299   if(script.IsEmpty()) return false;
00300 
00301   mainfunctionname = SCRIPT_LNG_LUA_MAINFUNCTIONNAME;
00302 
00303   int index = GetScript()->Find(mainfunctionname, false);
00304   if(index == XSTRING_NOTFOUND) return false;
00305 
00306   for(start=index; start>0; start--)
00307     {
00308       if((script.Get()[start] == __C('\r')) || (script.Get()[start] == __C('\n')))
00309         {
00310           break;
00311         }
00312     }
00313 
00314   for(end=index; end<(int)script.GetSize() ; end++)
00315     {
00316       if(script.Get()[end] == __C(')'))
00317         {
00318           break;
00319         }
00320     }
00321 
00322   script.Copy(start, end+1, line);
00323 
00324   line.DeleteCharacter(__C('\n'));
00325   line.DeleteCharacter(__C('\r'));
00326   line.DeleteCharacter(__C(' '));
00327 
00328   XSTRING searchmask;
00329 
00330   searchmask.Format(__L("function%s()"), mainfunctionname.Get());
00331 
00332   if(!line.Compare(searchmask)) return true;
00333 
00334   return false;
00335 }
