00365 int LUA_LibraryCallBack(lua_State* state)
00366 {
00367   lua_Debug    ar;
00368   XSTRING      namefunction;
00369   SCRIPT_LNG_LUA*  script = *static_cast<SCRIPT_LNG_LUA**>(lua_getextraspace(state));
00370 
00371   if(!script) 
00372     {
00373       return 0;
00374     }
00375 
00376   lua_getstack (state, 0, &ar);
00377   if(lua_getinfo(state, "Snlu", &ar))  namefunction = ar.name;
00378 
00379   if(namefunction.IsEmpty()) 
00380     {
00381       return 0;
00382     }
00383 
00384   SCRIPT_LIB_FUNCTION* libfunction = script->GetLibraryFunction(namefunction.Get());
00385   if(!libfunction) 
00386     {
00387       return 0;
00388     }
00389 
00390   if(!libfunction->GetFunctionLibrary()) 
00391     {
00392       return 0;
00393     }
00394 
00395   XVECTOR<XVARIANT*> params;
00396   XVARIANT           returnvalue;
00397 
00398   int nargs = lua_gettop(state);
00399 
00400   for(int c=1; c <= nargs; ++c)
00401     {
00402       XVARIANT* variant = new XVARIANT();
00403       if(variant)
00404         {
00405           switch(lua_type(state, c))
00406             {
00407               case LUA_TNONE          :                                                       break;
00408               case LUA_TNIL           : /*(*variant) = NULL;*/                                break;
00409               case LUA_TBOOLEAN       : (*variant) = (lua_toboolean(state, c)?true:false);    break;
00410               case LUA_TLIGHTUSERDATA :                                                       break;
00411               case LUA_TNUMBER        : /*
00412                                         {
00413                                           float  number   = (float)lua_tonumber(state, c);
00414                                           double intpart  = 0.0f;
00415                                           double fracpart = 0.0f;
00416 
00417                                           fracpart = modf(number, &intpart);
00418 
00419                                           if(!fracpart)
00420                                                   (*variant) = (int)(number);
00421                                             else  (*variant) = (float)(number);
00422                                         }
00423                                         */
00424 
00425                                         (*variant) = (double)lua_tonumber(state, c);
00426                                         break;
00427 
00428 
00429               case LUA_TSTRING        : (*variant) = (char*)(lua_tostring(state, c));         break;
00430               case LUA_TTABLE         :                                                       break;
00431               case LUA_TFUNCTION      : // XVARIANT don´t suppor pointers yet
00432                                         //(*variant) = (void*)(lua_tocfunction(state,c));
00433                                         break;
00434               case LUA_TUSERDATA      :                                                       break;
00435               case LUA_TTHREAD        :                                                       break;
00436               case LUA_NUMTAGS        :                                                       break;
00437             }
00438 
00439           params.Add(variant);
00440 
00441         }
00442     }
00443 
00444   libfunction->GetFunctionLibrary()(libfunction->GetLibrary(), script, &params, &returnvalue);
00445 
00446   params.DeleteContents();
00447   params.DeleteAll();
00448 
00449   // Push the return value on top of the stack. NOTE: We haven't popped the
00450   // input arguments to our function. To be honest, I haven't checked if we
00451   // must, but at least in stack machines like the JVM, the stack will be
00452   // cleaned between each function call.
00453 
00454   int nreturnvalues = 0;
00455 
00456   switch(returnvalue.GetType())
00457     {
00458       case XVARIANT_TYPE_NULL          : lua_pushnil (state);                          nreturnvalues++;     break;
00459       case XVARIANT_TYPE_BOOLEAN       : lua_pushboolean (state, (int)(returnvalue));  nreturnvalues++;     break;      
00460       case XVARIANT_TYPE_INTEGER       : lua_pushnumber(state, (int)(returnvalue));    nreturnvalues++;     break;
00461       case XVARIANT_TYPE_CHAR          : lua_pushnumber(state, (int)(returnvalue));    nreturnvalues++;     break;
00462       case XVARIANT_TYPE_XCHAR         :                                                                    break;
00463       case XVARIANT_TYPE_FLOAT         : lua_pushnumber(state, (float)(returnvalue));  nreturnvalues++;     break;
00464 
00465       case XVARIANT_TYPE_STRING        : { XSTRING stringreturnvalue;
00466 
00467                                            stringreturnvalue = (const XSTRING&)returnvalue;
00468 
00469                                            XBUFFER charstr;
00470                                            
00471                                            stringreturnvalue.ConvertToASCII(charstr);                                           
00472                                            lua_pushstring(state, charstr.GetPtrChar());
00473                                            
00474                                            nreturnvalues++;
00475                                          }
00476                                          break;
00477 
00478       case XVARIANT_TYPE_DATE          :                                                                    break;
00479       case XVARIANT_TYPE_TIME          :                                                                    break;
00480       case XVARIANT_TYPE_DATETIME      :                                                                    break;
00481     }
00482 
00483 
00484   // Let Lua know how many return values we've passed
00485   return nreturnvalues;
