00162 void Call_Key_Press(SCRIPT_LIB* library, SCRIPT* script, XVECTOR<XVARIANT*>* params, XVARIANT* returnvalue)
00163 {
00164   if(!library)      return;
00165   if(!script)       return;
00166   if(!params)       return;
00167   if(!returnvalue)  return;
00168 
00169   returnvalue->Set();
00170 
00171   bool status  = false;
00172 
00173   if(!params->GetSize())
00174     {
00175       script->HaveError(SCRIPT_ERRORCODE_INSUF_PARAMS);
00176       return;
00177     }
00178 
00179   int key = 0;
00180   
00181   library->GetParamConverted(params->Get(0), key);
00182   
00183   INPSIMULATE* inpsimulate = GEN_INPFACTORY.CreateSimulator();
00184   if(!inpsimulate)
00185     {
00186       (*returnvalue) = status;
00187       return;
00188     }
00189   
00190   status = inpsimulate->Key_Press((XBYTE)key);
00191 
00192   GEN_INPFACTORY.DeleteSimulator(inpsimulate);
00193 
00194   (*returnvalue) = status;
00195 }
