00935 bool IsSimilarPixel(XDWORD origin, XDWORD target, XBYTE margin)
00936 {
00937   XBYTE* originRGBA = (XBYTE*)&origin;
00938   XBYTE* targetRGBA = (XBYTE*)&target;  
00939   bool   status     = false;
00940 
00941   int ncomponent = 0;
00942 
00943   for(int c=0; c<4; c++)
00944     {
00945       if(abs(originRGBA[c] - targetRGBA[c]) > margin)
00946         {
00947           ncomponent++;
00948         }
00949     }
00950 
00951   if(!ncomponent) 
00952     {
00953       status = true;
00954     }  
00955 
00956   return status;
00957 }
